#include <shap/surface.hpp>
#include <iostream>
#include <iomanip>

using namespace shap;

void print_surface_info(const auto& surface, double u, double v, const char* label = nullptr) {
    if (label) std::cout << label << ":\n";
    std::cout << std::fixed << std::setprecision(6);

    auto p = surface(u, v);
    std::cout << "Point: (" << p.x << ", " << p.y << ", " << p.z << ")\n";

    auto n = surface.normal(u, v);
    std::cout << "Normal: (" << n.x << ", " << n.y << ", " << n.z << ")\n";

    auto metric = surface.metric_tensor(u, v);
    std::cout << "Metric tensor:\n";
    std::cout << "  [" << metric.g11 << " " << metric.g12 << "]\n";
    std::cout << "  [" << metric.g21 << " " << metric.g22 << "]\n";

    auto K = surface.gaussian_curvature(u, v);
    std::cout << "Gaussian curvature: " << K << "\n\n";
}

int main() {
    // Create a unit sphere
    constexpr auto sphere = surfaces::sphere(1.0);

    std::cout << "=== Sphere Analysis ===\n";
    // Check equator point
    std::cout << "At (u,v) = (0.000, 1.571) [equator point]:\n";
    print_surface_info(sphere, 0.0, M_PI/2);

    // Check north pole
    std::cout << "At (u,v) = (0.000, 0.000) [north pole]:\n";
    print_surface_info(sphere, 0.0, 0.0);

    // Create a cube with connected faces
    constexpr auto cube = surfaces::cube(1.0);

    std::cout << "\n=== Cube Analysis ===\n";

    // Analyze centers of faces
    print_surface_info(cube.front(), 0.5, 0.5, "Front face center");
    print_surface_info(cube.right(), 0.5, 0.5, "Right face center");

    // Analyze connection between front and right faces
    std::cout << "\nFront-Right Connection Analysis:\n";
    auto connection = cube.front_right();

    // Check connection at several points
    for (double t = 0.0; t <= 1.0; t += 0.25) {
        std::cout << "\nAt t = " << t << ":\n";
        auto [front_point, right_point] = connection.evaluate_connection(t);
        std::cout << "  Front edge: (" 
                  << front_point.x << ", " 
                  << front_point.y << ", " 
                  << front_point.z << ")\n";
        std::cout << "  Right edge: (" 
                  << right_point.x << ", " 
                  << right_point.y << ", " 
                  << right_point.z << ")\n";
        std::cout << "  Connection error: " 
                  << connection.connection_error(t) << "\n";
    }

    return 0;
}

