#pragma once
#include "types.hpp"
#include "point.hpp"
#include <functional>
#include <memory>
#include <optional>
#include <utility>
#include <stdexcept>

namespace shap {

class SurfacePoint;
class SurfacePath;

// Function types for surface creation
using PositionFunction = std::function<Point(double, double)>;
using DerivativeFunction = std::function<Point(double, double)>;
using CurvatureFunction = std::function<double(double, double)>;

// Path solver returns intersection with surface boundary
struct PathIntersection {
    double t;                // Distance to intersection
    Point position;          // Intersection point
    ParamIndex param;        // Which parameter (u/v) hit boundary
    ParamBound bound;        // Which bound (0/1) was hit
    double edge_parameter;   // Parameter along the edge

    // Constructor with validation
    PathIntersection(
        double t_,
        Point position_,
        ParamIndex param_,
        ParamBound bound_,
        double edge_parameter_
    ) : t(t_)
      , position(std::move(position_))
      , param(param_)
      , bound(bound_)
      , edge_parameter(edge_parameter_) {
        if (t_ < 0) {
            throw std::invalid_argument("Intersection distance must be non-negative");
        }
        if (edge_parameter_ < 0 || edge_parameter_ > 1) {
            throw std::invalid_argument("Edge parameter must be in [0,1]");
        }
    }
};

using PathSolver = std::function<std::optional<PathIntersection>(
    const Point& start,
    const Vector& direction,
    double max_t
)>;

// Geometric properties at a surface point
struct GeometricProperties {
    Point position;
    Vector normal;
    Vector du;
    Vector dv;
    std::optional<Point> duu;
    std::optional<Point> duv;
    std::optional<Point> dvv;
    std::optional<double> gaussian_curvature;
    std::optional<double> mean_curvature;
    std::optional<std::pair<double, double>> principal_curvatures;

    // Basic constructor with just position and derivatives
    GeometricProperties(
        Point pos,
        Vector n,
        Vector du_vec,
        Vector dv_vec
    ) noexcept
        : position(std::move(pos))
        , normal(std::move(n))
        , du(std::move(du_vec))
        , dv(std::move(dv_vec)) {}

    // Full constructor with all properties
    GeometricProperties(
        Point pos,
        Vector n,
        Vector du_vec,
        Vector dv_vec,
        Point duu_point,
        Point duv_point,
        Point dvv_point,
        double gaussian,
        double mean,
        std::pair<double, double> principal
    ) noexcept
        : position(std::move(pos))
        , normal(std::move(n))
        , du(std::move(du_vec))
        , dv(std::move(dv_vec))
        , duu(std::move(duu_point))
        , duv(std::move(duv_point))
        , dvv(std::move(dvv_point))
        , gaussian_curvature(gaussian)
        , mean_curvature(mean)
        , principal_curvatures(std::move(principal)) {}
};

class Surface {
public:
    virtual ~Surface() = default;

    // Prevent copying
    Surface(const Surface&) = delete;
    Surface& operator=(const Surface&) = delete;

    // Allow moving
    Surface(Surface&&) noexcept = default;
    Surface& operator=(Surface&&) noexcept = default;

protected:
    Surface() = default;

public:
    // Evaluate surface at parameter values
    [[nodiscard]] SurfacePoint evaluate(double u, double v) const;

    /**
     * Create a path on the surface starting from a point in a given direction.
     * See implementation comments for detailed requirements.
     *
     * @param start Starting point on the surface
     * @param direction Desired world-space direction (will be projected onto surface)
     * @param length Desired path length in world space units
     * @throws std::invalid_argument if preconditions are not met
     * @return Unique pointer to path object representing the curve
     */
    [[nodiscard]] virtual std::unique_ptr<SurfacePath> create_path(
        const SurfacePoint& start,
        const Vector& direction,
        double length
    ) const;

    // Get geometric properties at parameter values
    [[nodiscard]] virtual GeometricProperties compute_properties(double u, double v) const = 0;

    // Get path solver if available
    [[nodiscard]] virtual std::optional<PathSolver> get_path_solver() const noexcept {
        return std::nullopt;
    }

    // Get surface type
    [[nodiscard]] virtual SurfaceType surface_type() const noexcept {
        return SurfaceType::Generic;
    }

    /**
     * Convert a world space position to parameter space coordinates.
     * See implementation comments for detailed requirements.
     *
     * @param pos World space position to convert
     * @throws std::invalid_argument if pos is too far from surface
     * @return Parameter space coordinates (u,v) in [0,1]×[0,1]
     */
    [[nodiscard]] virtual std::pair<double, double> world_to_parameters(const Point& pos) const = 0;

    // Helper to convert world direction to parameter space
    [[nodiscard]] std::pair<double, double> compute_parameter_derivatives(
        const Vector& direction,
        const Point& du,
        const Point& dv
    ) const noexcept;

protected:
    // Validate parameter values are in [0,1]
    static void validate_parameters(double u, double v) {
        if (u < 0.0 || u > 1.0 || v < 0.0 || v > 1.0) {
            throw std::invalid_argument("Surface parameters must be in [0,1]");
        }
    }

public:
    // Factory methods
    [[nodiscard]] static std::shared_ptr<Surface> create(
        PositionFunction position_func,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Generic
    );

    [[nodiscard]] static std::shared_ptr<Surface> create_with_derivatives(
        PositionFunction position_func,
        DerivativeFunction du_func,
        DerivativeFunction dv_func,
        std::optional<CurvatureFunction> gaussian_curv = std::nullopt,
        std::optional<CurvatureFunction> mean_curv = std::nullopt,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Smooth
    );

    [[nodiscard]] static std::shared_ptr<Surface> create_with_all_derivatives(
        PositionFunction position_func,
        DerivativeFunction du_func,
        DerivativeFunction dv_func,
        DerivativeFunction duu_func,
        DerivativeFunction duv_func,
        DerivativeFunction dvv_func,
        std::optional<CurvatureFunction> gaussian_curv = std::nullopt,
        std::optional<CurvatureFunction> mean_curv = std::nullopt,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Smooth
    );
};

