#include "shap/surface.hpp"
#include "shap/surface_point.hpp"
#include "shap/path.hpp"
#include <limits>
#include <cmath>
#include <array>

namespace shap {

namespace {
    constexpr double EPSILON = 1e-10;
    constexpr int GRID_SIZE = 10;
    constexpr int MAX_ITERATIONS = 20;
    constexpr double GRADIENT_STEP = 0.01;
    constexpr int PATH_POINTS = 10;

    // Adaptive step size for numerical derivatives
    [[nodiscard]] constexpr double compute_step_size(double x) noexcept {
        const double eps = std::numeric_limits<double>::epsilon();
        return std::cbrt(eps) * (1.0 + std::abs(x));
    }

    // Helper to compute normal from derivatives
    [[nodiscard]] Vector compute_normal(const Point& du, const Point& dv) noexcept {
        return cross(du, dv).normalize();
    }

    // Helper to compute curvature coefficients
    struct CurvatureCoefficients {
        double E, F, G;  // First fundamental form
        double L, M, N;  // Second fundamental form
        double det;      // EG - F²

        [[nodiscard]] static CurvatureCoefficients compute(
            const Point& du,
            const Point& dv,
            const Point& duu,
            const Point& duv,
            const Point& dvv,
            const Vector& normal
        ) noexcept {
            CurvatureCoefficients coeff;
            coeff.E = dot(du, du);
            coeff.F = dot(du, dv);
            coeff.G = dot(dv, dv);
            coeff.L = dot(duu, normal);
            coeff.M = dot(duv, normal);
            coeff.N = dot(dvv, normal);
            coeff.det = coeff.E * coeff.G - coeff.F * coeff.F;
            return coeff;
        }

        [[nodiscard]] std::optional<double> gaussian_curvature() const noexcept {
            if (std::abs(det) <= EPSILON) return std::nullopt;
            return (L * N - M * M) / det;
        }

        [[nodiscard]] std::optional<double> mean_curvature() const noexcept {
            if (std::abs(det) <= EPSILON) return std::nullopt;
            return (E * N - 2.0 * F * M + G * L) / (2.0 * det);
        }
    };
} // anonymous namespace

class FunctionSurface final : public Surface {
public:
    FunctionSurface(
        PositionFunction pos,
        std::optional<DerivativeFunction> du = std::nullopt,
        std::optional<DerivativeFunction> dv = std::nullopt,
        std::optional<DerivativeFunction> duu = std::nullopt,
        std::optional<DerivativeFunction> duv = std::nullopt,
        std::optional<DerivativeFunction> dvv = std::nullopt,
        std::optional<CurvatureFunction> gaussian = std::nullopt,
        std::optional<CurvatureFunction> mean = std::nullopt,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Smooth
    ) noexcept
        : position_func_(std::move(pos))
        , du_func_(std::move(du))
        , dv_func_(std::move(dv))
        , duu_func_(std::move(duu))
        , duv_func_(std::move(duv))
        , dvv_func_(std::move(dvv))
        , gaussian_curv_func_(std::move(gaussian))
        , mean_curv_func_(std::move(mean))
        , path_solver_(std::move(path_solver))
        , type_(type) {}

    [[nodiscard]] GeometricProperties compute_properties(double u, double v) const override {
        validate_parameters(u, v);

        const Point position = position_func_(u, v);
        Point du, dv;

        // Compute first derivatives
        if (du_func_ && dv_func_) {
            du = (*du_func_)(u, v);
            dv = (*dv_func_)(u, v);
        } else {
            // Adaptive step size numerical derivatives
            const double hu = compute_step_size(u);
            const double hv = compute_step_size(v);

            du = (position_func_(u + hu, v) - position_func_(u - hu, v)) * (0.5 / hu);
            dv = (position_func_(u, v + hv) - position_func_(u, v - hv)) * (0.5 / hv);
        }

        const Vector normal = compute_normal(du, dv);

        // Create properties with first derivatives
        GeometricProperties props(
            std::move(position),
            normal,
            std::move(du),
            std::move(dv)
        );

        // Compute second derivatives if needed for smooth surfaces
        if (type_ == SurfaceType::Smooth) {
            Point duu, duv, dvv;

            if (duu_func_) {
                duu = (*duu_func_)(u, v);
            } else {
                const double hu = compute_step_size(u);
                duu = (position_func_(u + hu, v) - 2.0 * props.position + 
                      position_func_(u - hu, v)) * (1.0 / (hu * hu));
            }

            if (duv_func_) {
                duv = (*duv_func_)(u, v);
            } else {
                const double hu = compute_step_size(u);
                const double hv = compute_step_size(v);
                duv = ((position_func_(u + hu, v + hv) - position_func_(u + hu, v - hv)) -
                      (position_func_(u - hu, v + hv) - position_func_(u - hu, v - hv))) * 
                     (0.25 / (hu * hv));
            }

            if (dvv_func_) {
                dvv = (*dvv_func_)(u, v);
            } else {
                const double hv = compute_step_size(v);
                dvv = (position_func_(u, v + hv) - 2.0 * props.position + 
                      position_func_(u, v - hv)) * (1.0 / (hv * hv));
            }

            // Compute curvature
            const auto coeffs = CurvatureCoefficients::compute(
                props.du, props.dv, duu, duv, dvv, props.normal);

            if (gaussian_curv_func_) {
                props.gaussian_curvature = (*gaussian_curv_func_)(u, v);
            } else if (auto k = coeffs.gaussian_curvature()) {
                props.gaussian_curvature = *k;
            }

            if (mean_curv_func_) {
                props.mean_curvature = (*mean_curv_func_)(u, v);
            } else if (auto h = coeffs.mean_curvature()) {
                props.mean_curvature = *h;
            }

            // Compute principal curvatures if possible
            if (props.gaussian_curvature && props.mean_curvature) {
                const double H = *props.mean_curvature;
                const double K = *props.gaussian_curvature;
                const double disc = H*H - K;

                if (disc >= 0) {
                    const double sqrt_disc = std::sqrt(disc);
                    if (H >= 0) {
                        const double k1 = H + sqrt_disc;
                        const double k2 = K / k1;  // More stable than H - sqrt_disc
                        props.principal_curvatures = std::make_pair(k1, k2);
                    } else {
                        const double k2 = H - sqrt_disc;
                        const double k1 = K / k2;  // More stable than H + sqrt_disc
                        props.principal_curvatures = std::make_pair(k1, k2);
                    }
                }
            }
        }

        return props;
    }

    [[nodiscard]] std::optional<PathSolver> get_path_solver() const noexcept override {
        return path_solver_;
    }

    [[nodiscard]] SurfaceType surface_type() const noexcept override {
        return type_;
    }

    [[nodiscard]] std::pair<double, double> world_to_parameters(const Point& pos) const override {
        // Grid search for initial guess
        double best_u = 0, best_v = 0;
        double min_dist = std::numeric_limits<double>::max();

        for (int i = 0; i <= GRID_SIZE; ++i) {
            const double u = static_cast<double>(i) / GRID_SIZE;
            for (int j = 0; j <= GRID_SIZE; ++j) {
                const double v = static_cast<double>(j) / GRID_SIZE;
                const Point surface_pt = position_func_(u, v);
                const double dist = (surface_pt - pos).length_squared();
                if (dist < min_dist) {
                    min_dist = dist;
                    best_u = u;
                    best_v = v;
                }
            }
        }

        // Gradient descent refinement
        for (int iter = 0; iter < MAX_ITERATIONS; ++iter) {
            const Point curr_pos = position_func_(best_u, best_v);
            const Vector diff = pos - curr_pos;
            if (diff.length_squared() < EPSILON) break;

            // Compute numerical derivatives
            const double hu = compute_step_size(best_u);
            const double hv = compute_step_size(best_v);

            const Point du = (position_func_(best_u + hu, best_v) - curr_pos) * (1.0/hu);
            const Point dv = (position_func_(best_u, best_v + hv) - curr_pos) * (1.0/hv);

            // Update parameters
            best_u = std::clamp(best_u + GRADIENT_STEP * dot(diff, du), 0.0, 1.0);
            best_v = std::clamp(best_v + GRADIENT_STEP * dot(diff, dv), 0.0, 1.0);
        }

        return {best_u, best_v};
    }

private:
    PositionFunction position_func_;
    std::optional<DerivativeFunction> du_func_;
    std::optional<DerivativeFunction> dv_func_;
    std::optional<DerivativeFunction> duu_func_;
    std::optional<DerivativeFunction> duv_func_;
    std::optional<DerivativeFunction> dvv_func_;
    std::optional<CurvatureFunction> gaussian_curv_func_;
    std::optional<CurvatureFunction> mean_curv_func_;
    std::optional<PathSolver> path_solver_;
    SurfaceType type_;
};

SurfacePoint Surface::evaluate(double u, double v) const {
    validate_parameters(u, v);
    const GeometricProperties props = compute_properties(u, v);
    return SurfacePoint(
        const_cast<Surface*>(this),
        u, v,
        props.position,
        props.normal,
        props.du,
        props.dv
    );
}

std::unique_ptr<SurfacePath> Surface::create_path(
    const SurfacePoint& start,
    const Vector& direction,
    double length
) const {
    if (length <= 0) {
        throw std::invalid_argument("Path length must be positive");
    }
    if (direction.length_squared() < EPSILON) {
        throw std::invalid_argument("Direction vector cannot be zero");
    }

    auto path = std::make_unique<PathSegment>(
        std::shared_ptr<Surface>(const_cast<Surface*>(this), [](Surface*){})
    );

    // Project direction onto surface tangent plane
    const auto start_props = compute_properties(start.u(), start.v());
    Vector tangent_dir = direction - dot(direction, start_props.normal) * start_props.normal;
    if (tangent_dir.length_squared() < EPSILON) {
        throw std::runtime_error("Direction is perpendicular to surface");
    }
    tangent_dir = tangent_dir.normalize();

    // Try path solver first for surface transitions
    if (auto solver = get_path_solver()) {
        if (auto intersection = (*solver)(start.position(), direction, length)) {
            // Convert end point to parameter space
            const auto end_params = world_to_parameters(intersection->position);
            const auto start_params = std::make_pair(start.u(), start.v());

            path->add_point(0.0, start_params.first, start_params.second);

            // Sample points up to intersection
            for (int i = 1; i < PATH_POINTS; ++i) {
                const double alpha = static_cast<double>(i) / (PATH_POINTS - 1);
                const double t = intersection->t * alpha;

                // Linear interpolation in parameter space
                const double u = start_params.first + (end_params.first - start_params.first) * alpha;
                const double v = start_params.second + (end_params.second - start_params.second) * alpha;

                path->add_point(t, u, v);
            }
            return path;
        }
    }

    // For straight paths, compute end point in world space
    const Point end_pos = start.position() + tangent_dir * length;

    // Convert start and end points to parameter space
    const auto start_params = std::make_pair(start.u(), start.v());
    const auto end_params = world_to_parameters(end_pos);

    path->add_point(0.0, start_params.first, start_params.second);

    // Sample points along parameter space line
    for (int i = 1; i < PATH_POINTS; ++i) {
        const double alpha = static_cast<double>(i) / (PATH_POINTS - 1);

        // Linear interpolation in parameter space
        const double u = start_params.first + (end_params.first - start_params.first) * alpha;
        const double v = start_params.second + (end_params.second - start_params.second) * alpha;

        // Use actual world space distance for t
        const Point pos = evaluate(u, v).position();
        const double t = (pos - start.position()).length();

        path->add_point(t, u, v);
    }

    return path;
}

std::pair<double, double> Surface::compute_parameter_derivatives(
    const Vector& direction,
    const Point& du,
    const Point& dv
) const noexcept {
    const double du_du = dot(du, du);
    const double du_dv = dot(du, dv);
    const double dv_dv = dot(dv, dv);
    const double det = du_du * dv_dv - du_dv * du_dv;

    // Handle near-singular cases
    if (std::abs(det) < EPSILON) {
        // Project onto dominant direction
        if (du_du > dv_dv) {
            return {dot(direction, du.normalize()), 0.0};
        } else {
            return {0.0, dot(direction, dv.normalize())};
        }
    }

    const double inv_det = 1.0 / det;
    const double dir_du = dot(direction, du);
    const double dir_dv = dot(direction, dv);

    return {
        (dir_du * dv_dv - dir_dv * du_dv) * inv_det,
        (dir_dv * du_du - dir_du * du_dv) * inv_det
    };
}

std::shared_ptr<Surface> Surface::create(
    PositionFunction position_func,
    std::optional<PathSolver> path_solver,
    SurfaceType type
) {
    if (!position_func) {
        throw std::invalid_argument("Position function cannot be null");
    }
    return std::make_shared<FunctionSurface>(
        std::move(position_func),
        std::nullopt, std::nullopt,
        std::nullopt, std::nullopt, std::nullopt,
        std::nullopt, std::nullopt,
        std::move(path_solver),
        type
    );
}

std::shared_ptr<Surface> Surface::create_with_derivatives(
    PositionFunction position_func,
    DerivativeFunction du_func,
    DerivativeFunction dv_func,
    std::optional<CurvatureFunction> gaussian_curv,
    std::optional<CurvatureFunction> mean_curv,
    std::optional<PathSolver> path_solver,
