// Print utilities
namespace {
    constexpr int PRECISION = 6;
    constexpr double SAMPLE_INTERVAL = 0.1;

    // Helper to print section headers
    void print_section(std::string_view title) {
        std::cout << "\n=== " << title << " ===\n\n";
