#include <algorithm>

namespace shap {

namespace {
    constexpr double EPSILON = 1e-10;
    constexpr int MAX_TRANSITIONS = 8;  // Prevent infinite loops

    // Helper to compute parameter distance between edges
    [[nodiscard]] double compute_edge_distance(
        const EdgeDescriptor& edge1,
        const EdgeDescriptor& edge2
    ) noexcept {
        const int param_diff = static_cast<int>(edge1.param) - static_cast<int>(edge2.param);
        const int bound_diff = static_cast<int>(edge1.bound) - static_cast<int>(edge2.bound);
        return std::abs(param_diff) + std::abs(bound_diff);
    }
}

void SurfaceCollection::ConnectionBuilder::build() {
    if (!collection_ || !surface1_ || !surface2_) {
        throw std::runtime_error("Invalid connection specification: null pointer");
    }

    // Validate surfaces are different
    if (surface1_ == surface2_) {
        throw std::invalid_argument("Cannot connect a surface to itself");
    }

    collection_->create_connection(
        surface1_, surface2_,
        edge1_, edge2_,
        orientation_
    );
}

void SurfaceCollection::create_connection(
    Surface* s1, Surface* s2,
    EdgeDescriptor e1, EdgeDescriptor e2,
    int orientation
) {
    // Validate inputs
    if (!s1 || !s2) {
        throw std::invalid_argument("Cannot create connection with null surface");
    }

    // Check for existing connection
    const auto it = std::find_if(connections_.begin(), connections_.end(),
        [s1, s2](const auto& conn) { return conn.connects(s1, s2); });

    if (it != connections_.end()) {
        throw std::invalid_argument("Connection already exists between surfaces");
    }

    connections_.emplace_back(s1, s2, EdgeConnection{e1, e2, orientation});
}

SurfacePoint SurfaceConnection::map_point(const SurfacePoint& point) const {
    if (!point.surface()) {
        throw std::invalid_argument("Cannot map point with null surface");
    }

    Surface* const target = other_surface(point.surface());
    const EdgeDescriptor target_edge = other_edge(point.surface());

    // Map the edge parameter with proper orientation
    double edge_param = point.edge_parameter();
    if (point.surface() == surface2_) {  // Going from surface2 to surface1
        edge_param = edge_conn_.map_parameter(edge_param);
    }

    // Get target surface parameters
    const auto [u, v] = target_edge.get_params(edge_param);

    // Create point on target surface
    auto target_point = target->evaluate(u, v);

    // Ensure consistent normal orientation
    if (dot(target_point.normal(), point.normal()) < 0) {
        return SurfacePoint(
            target_point.surface(),
            target_point.u(),
            target_point.v(),
            target_point.position(),
            -target_point.normal(),
            target_point.du(),
            target_point.dv(),
            target_point.edge(),
            target_point.path_param()
        );
    }
    return target_point;
}

namespace {
    // Helper to check if a point is on a surface edge
    [[nodiscard]] bool is_on_edge(const SurfacePoint& point, double epsilon = EPSILON) noexcept {
        if (!point.edge()) return false;

        // Check if u or v is at 0 or 1 within epsilon
        const double u = point.u();
        const double v = point.v();
        return (std::abs(u) < epsilon || std::abs(u - 1.0) < epsilon ||
                std::abs(v) < epsilon || std::abs(v - 1.0) < epsilon);
    }
}

std::unique_ptr<SurfacePath> SurfaceCollection::create_path(
    const SurfacePoint& start,
    const Vector& direction,
    double length
) const {
    // Validate inputs
    if (!start.surface()) {
        throw std::invalid_argument("Invalid starting surface");
    }
    if (length <= 0) {
        throw std::invalid_argument("Path length must be positive");
    }
    if (direction.length_squared() < EPSILON) {
        throw std::invalid_argument("Direction vector cannot be zero");
    }
    if (!is_on_edge(start)) {
        throw std::invalid_argument("Starting point must be on a surface edge for transitions");
    }

    auto path = std::make_unique<TransitionPath>();

    Surface* current_surface = start.surface();
    Surface* const start_surface = current_surface;

    double t = 0.0;
    SurfacePoint current = start;
    Vector current_dir = direction.normalize();  // Ensure normalized
    bool completed_circuit = false;
    int transition_count = 0;

    while (t < length && !completed_circuit && transition_count < MAX_TRANSITIONS) {
        // Try to find intersection with current surface boundary
        std::optional<PathIntersection> intersection;
        if (auto solver = current_surface->get_path_solver()) {
            intersection = (*solver)(current.position(), current_dir, length - t);
        }

        if (intersection) {
            // Get current surface properties
            const auto props = current_surface->compute_properties(current.u(), current.v());

            // Convert world direction to parameter space
            const auto [du_param, dv_param] = current_surface->compute_parameter_derivatives(
                current_dir, props.du, props.dv);

            // Compute end parameters
            const double u_end = current.u() + du_param * intersection->t;
            const double v_end = current.v() + dv_param * intersection->t;

            // Add segment up to intersection
            path->add_segment(
                std::shared_ptr<Surface>(current_surface, [](Surface*){}),
                t, t + intersection->t,
                current.u(), u_end,
                current.v(), v_end,
                current_dir
            );

            // Find best connection at intersection point
            const SurfaceConnection* next_connection = nullptr;
            double min_param_dist = std::numeric_limits<double>::infinity();

            const EdgeDescriptor intersection_edge{
                intersection->param,
                intersection->bound,
                intersection->edge_parameter
            };

            for (const auto& conn : connections_) {
                if (conn.surface1() == current_surface || conn.surface2() == current_surface) {
                    // Check both possible edge matches
                    if (current_surface == conn.surface1()) {
                        const double param_dist = compute_edge_distance(
                            intersection_edge, conn.edge_connection().edge1);
                        if (param_dist < min_param_dist) {
                            min_param_dist = param_dist;
                            next_connection = &conn;
                        }
                    }
                    if (current_surface == conn.surface2()) {
                        const double param_dist = compute_edge_distance(
                            intersection_edge, conn.edge_connection().edge2);
                        if (param_dist < min_param_dist) {
                            min_param_dist = param_dist;
                            next_connection = &conn;
                        }
                    }
                }
            }

            if (!next_connection || min_param_dist > EPSILON) {
                // Hit boundary but no valid connection found
                break;
            }

            // Create transition point and map to next surface
            const auto trans_point = current_surface->evaluate(u_end, v_end);
            current = next_connection->map_point(trans_point);
            current_surface = current.surface();
            transition_count++;

            // Check if we've completed a circuit
            if (current_surface == start_surface && transition_count > 1) {
                completed_circuit = true;
            }

            // Update direction to maintain consistent movement
            const bool entering_surface1 = (current_surface == next_connection->surface1());
            const auto& target_edge = entering_surface1 ? 
                next_connection->edge_connection().edge1 : 
                next_connection->edge_connection().edge2;

            // Compute new direction based on surface derivatives
            const auto new_props = current_surface->compute_properties(current.u(), current.v());
            const Vector edge_dir = (target_edge.free_param() == ParamIndex::U) ?
                new_props.du : new_props.dv;

            // Project original direction onto new surface
            const Vector& normal = new_props.normal;
            current_dir = (direction - dot(direction, normal) * normal).normalize();

            // Ensure consistent movement direction
            if (dot(current_dir, edge_dir) < 0) {
                current_dir = -current_dir;
            }

            t += intersection->t;
        } else {
            // No intersection found, add segment to end
            const double remaining = length - t;

            // Get surface derivatives and convert direction
            const auto props = current_surface->compute_properties(current.u(), current.v());
            const auto [du_param, dv_param] = current_surface->compute_parameter_derivatives(
                current_dir, props.du, props.dv);

            // Compute end parameters
            const double u_end = current.u() + du_param * remaining;
            const double v_end = current.v() + dv_param * remaining;

            path->add_segment(
                std::shared_ptr<Surface>(current_surface, [](Surface*){}),
                t, t + remaining,
                current.u(), u_end,
                current.v(), v_end,
                current_dir
            );
            break;
        }
    }

    return path;
}

