#pragma once
#include "../surface.hpp"
#include <cmath>
#include <stdexcept>

namespace shap {
namespace surfaces {

/**
 * A planar square face defined by an origin point and two edge vectors.
 *
 * Parameter Space Mapping:
 * - Domain: (u,v) ∈ [0,1]×[0,1]
 * - Range: 3D rectangle defined by origin, du, dv vectors
 * - Formula: P(u,v) = origin + u*du + v*dv
 *
 * Length Scaling:
 * - du defines scale in u direction (e.g., 2 units wide)
 * - dv defines scale in v direction (e.g., 2 units tall)
 * - World space distance must be converted to parameter space
 * - Example: If |du| = 2 and we want to move 1 unit in du direction:
 *   * World space: distance = 1
 *   * Parameter space: Δu = 1/|du| = 0.5
 *
 * Properties:
 * - Linear mapping from parameters to world space
 * - Face normal is constant (cross product of du and dv)
 * - All curvatures are zero (planar surface)
 * - Geodesics are straight lines
 * - Path length scales with du/dv magnitudes
 */
class SquareFace final : public Surface {
public:
    /**
     * Create a square face from origin point and edge vectors.
     *
     * @param origin Corner point where u=v=0
     * @param du Vector from origin to u=1 (defines u direction and scale)
     * @param dv Vector from origin to v=1 (defines v direction and scale)
     * @param vector_length_epsilon Tolerance for vector length checks (default: 1e-10)
     * @param vector_parallel_epsilon Tolerance for parallel vector check (default: 1e-10)
     * @param parameter_bound_epsilon Tolerance for parameter bounds (default: 1e-10)
     * @throws std::invalid_argument if vectors are invalid or if any epsilon <= 0
     */
    explicit SquareFace(
        Point origin,
        Vector du,
        Vector dv,
        double vector_length_epsilon = 1e-10,
        double vector_parallel_epsilon = 1e-10,
        double parameter_bound_epsilon = 1e-10
    ) : origin_(std::move(origin))
      , du_(std::move(du))
      , dv_(std::move(dv)) {
        if (vector_length_epsilon <= 0 || vector_parallel_epsilon <= 0 || parameter_bound_epsilon <= 0) {
            throw std::invalid_argument("Epsilon values must be positive");
        }
        vector_length_epsilon_ = vector_length_epsilon;
        vector_parallel_epsilon_ = vector_parallel_epsilon;
        parameter_bound_epsilon_ = parameter_bound_epsilon;
        validate_vectors();
        normal_ = cross(du_, dv_).normalize();
        setup_path_solver();
    }

    // Move operations
    SquareFace(SquareFace&&) noexcept = default;
    SquareFace& operator=(SquareFace&&) noexcept = default;

    // Prevent copying
    SquareFace(const SquareFace&) = delete;
    SquareFace& operator=(const SquareFace&) = delete;

    // Inverse of P(u,v) = origin + u*du + v*dv
    [[nodiscard]] std::pair<double, double> world_to_parameters(const Point& pos) const override {
        // Solve linear system: pos - origin = u*du + v*dv
        const Vector rel_pos = pos - origin_;

        // Use Cramer's rule for 2x2 system
        const double det = cross(du_, dv_).length();
        if (det > vector_length_epsilon_) {
            // Regular case - vectors form valid basis
            return {
                dot(cross(rel_pos, dv_), normal_) / det,  // u coordinate
                dot(cross(du_, rel_pos), normal_) / det   // v coordinate
            };
        }

        // Degenerate case - project onto dominant direction
        return {
            dot(rel_pos, du_.normalize()),  // u coordinate
            dot(rel_pos, dv_.normalize())   // v coordinate
        };
    }

    // Map parameters [0,1]×[0,1] directly to world space
    [[nodiscard]] GeometricProperties compute_properties(double u, double v) const override {
        validate_parameters(u, v);

        // Linear mapping from parameter space to world space
        const Point position = origin_ + du_ * u + dv_ * v;

        // Properties are constant for a planar face
        return GeometricProperties(
            position,           // Position is linear in u,v
            normal_,           // Normal is constant
            du_,              // d/du is constant
            dv_,              // d/dv is constant
            Point(),          // d²/du² = 0
            Point(),          // d²/dudv = 0
            Point(),          // d²/dv² = 0
            0.0,              // Gaussian curvature = 0
            0.0,              // Mean curvature = 0
            std::make_pair(0.0, 0.0)  // Principal curvatures = 0
        );
    }

    [[nodiscard]] std::optional<PathSolver> get_path_solver() const noexcept override {
        return path_solver_;
    }

    [[nodiscard]] SurfaceType surface_type() const noexcept override {
        return SurfaceType::Developable;
    }

    // Access geometry
    [[nodiscard]] const Point& origin() const noexcept { return origin_; }
    [[nodiscard]] const Vector& du() const noexcept { return du_; }
    [[nodiscard]] const Vector& dv() const noexcept { return dv_; }
    [[nodiscard]] const Vector& normal() const noexcept { return normal_; }

private:
    // Validate edge vectors
    void validate_vectors() {
        if (du_.length_squared() < vector_length_epsilon_) {
            throw std::invalid_argument("du vector cannot be zero");
        }
        if (dv_.length_squared() < vector_length_epsilon_) {
            throw std::invalid_argument("dv vector cannot be zero");
        }
        if (std::abs(dot(du_.normalize(), dv_.normalize())) > 1.0 - vector_parallel_epsilon_) {
            throw std::invalid_argument("du and dv vectors cannot be parallel");
        }
    }

    // Helper to check intersection with parameter bound
    [[nodiscard]] bool check_intersection(
        double curr_param,
        double d_param,
        double bound_val,
        ParamIndex param,
        ParamBound bound,
        double other_param,
        const std::pair<double, double>& param_dir,
        double param_length,
        double& min_t,
        ParamIndex& hit_param,
        ParamBound& hit_bound,
        double& edge_param
    ) const noexcept {
        if (std::abs(d_param) > parameter_bound_epsilon_) {
            const double t = (bound_val - curr_param) / d_param;
            if (t > 0 && t < min_t) {
                // Check if intersection point is within other parameter bounds
                const double other_at_t = other_param + param_dir.second * t / param_length;
                if (other_at_t >= -parameter_bound_epsilon_ && other_at_t <= 1.0 + parameter_bound_epsilon_) {
                    min_t = t;
                    hit_param = param;
                    hit_bound = bound;
                    edge_param = std::clamp(other_at_t, 0.0, 1.0);
                    return true;
                }
            }
        }
        return false;
    }

    void setup_path_solver() noexcept {
        path_solver_ = [this](const Point& start, const Vector& dir, double max_t)
            -> std::optional<PathIntersection> {
            // Project direction onto face plane
            Vector planar_dir = dir - dot(dir, normal_) * normal_;
            const double planar_length = planar_dir.length();
            if (planar_length < vector_length_epsilon_) {
                return std::nullopt;  // Direction perpendicular to face
            }
            planar_dir = planar_dir * (1.0 / planar_length);

            // Convert start point and direction to parameter space
            const auto start_params = world_to_parameters(start);
            const auto end_params = world_to_parameters(start + planar_dir);
            const auto param_dir = std::make_pair(
                end_params.first - start_params.first,
                end_params.second - start_params.second
            );

            // Normalize parameter space direction
            const double param_length = std::sqrt(
                param_dir.first * param_dir.first +
                param_dir.second * param_dir.second
            );
            if (param_length < parameter_bound_epsilon_) return std::nullopt;

            // Find earliest intersection with parameter bounds
            double min_t = max_t;
            ParamIndex hit_param = ParamIndex::U;
            ParamBound hit_bound = ParamBound::Lower;
            double edge_param = 0.0;
            bool found = false;

            // Check all bounds using normalized parameter direction
            found |= check_intersection(
                start_params.first, param_dir.first / param_length, 0,
                ParamIndex::U, ParamBound::Lower, start_params.second,
                param_dir, param_length, min_t, hit_param, hit_bound, edge_param
            );
            found |= check_intersection(
                start_params.first, param_dir.first / param_length, 1,
                ParamIndex::U, ParamBound::Upper, start_params.second,
                param_dir, param_length, min_t, hit_param, hit_bound, edge_param
            );
            found |= check_intersection(
                start_params.second, param_dir.second / param_length, 0,
                ParamIndex::V, ParamBound::Lower, start_params.first,
                param_dir, param_length, min_t, hit_param, hit_bound, edge_param
            );
            found |= check_intersection(
                start_params.second, param_dir.second / param_length, 1,
                ParamIndex::V, ParamBound::Upper, start_params.first,
                param_dir, param_length, min_t, hit_param, hit_bound, edge_param
            );

            if (!found) return std::nullopt;

            // Convert parameter space distance to world space
            const double world_t = min_t * (hit_param == ParamIndex::U ? du_.length() : dv_.length());

            // Compute intersection position using parameter space mapping
            const double u = hit_param == ParamIndex::U ? 
                static_cast<double>(hit_bound) : start_params.first;
            const double v = hit_param == ParamIndex::V ? 
                static_cast<double>(hit_bound) : start_params.second;
            const Point position = origin_ + du_ * u + dv_ * v;

            return PathIntersection(
                world_t,
                position,
                hit_param,
                hit_bound,
                edge_param
            );
        };
    }

    Point origin_;
    Vector du_;
    Vector dv_;
    Vector normal_;
    double vector_length_epsilon_;
    double vector_parallel_epsilon_;
    double parameter_bound_epsilon_;
    PathSolver path_solver_;
};

/**
 * Create a square face with the given origin and edge vectors.
 * 
 * @param origin Corner point where u=v=0
 * @param du Vector from origin to u=1
 * @param dv Vector from origin to v=1
 * @param vector_length_epsilon Tolerance for vector length checks (default: 1e-10)
 * @param vector_parallel_epsilon Tolerance for parallel vector check (default: 1e-10)
 * @param parameter_bound_epsilon Tolerance for parameter bounds (default: 1e-10)
 * @return Shared pointer to square face
 * @throws std::invalid_argument if vectors are invalid or if any epsilon <= 0
 */
[[nodiscard]] inline std::shared_ptr<Surface> create_square_face(
    Point origin,
    Vector du,
    Vector dv,
    double vector_length_epsilon = 1e-10,
    double vector_parallel_epsilon = 1e-10,
    double parameter_bound_epsilon = 1e-10
) {
    return std::make_shared<SquareFace>(
        std::move(origin),
        std::move(du),
        std::move(dv),
        vector_length_epsilon,
        vector_parallel_epsilon,
        parameter_bound_epsilon
    );
}

