// Test path transitions between cube faces
void test_cube_face_transitions() {
    std::cout << "\nTesting cube face transitions...\n";
    CubeTest test;

    // Test 1: Front to right transition
    {
        std::cout << "\nTest 1: Front to right transition\n";
        auto front = test.cube_.get_surface("front");
        assert(front);

        // World space path: from (0.5, 1, 0) to (1.5, 0.5, 0)
        const Point world_start(0.5, 1, 0);
        const Vector world_dir = Vector(1, -0.5, 0).normalize();
        constexpr double world_length = std::sqrt(1.25);

        // Convert to parameter space for front face
        const auto [u_start, v_start] = CubeFaceTest::world_to_params(world_start);

        std::cout << "World space:\n";
        print_point("  Start", world_start);
        std::cout << "  Direction: (" << world_dir.x << ", " 
                 << world_dir.y << ", " << world_dir.z << ")\n";
        std::cout << "  Length: " << world_length << "\n";
        std::cout << "Parameter space:\n";
        std::cout << "  Start: (u=" << u_start << ", v=" << v_start << ")\n";

        // Create and evaluate path
        const auto start_params = front->evaluate(u_start, v_start);
        print_point("Start params position", start_params.position());

        auto path = test.cube_.create_path(start_params, world_dir, world_length);

        // Sample points along path
        std::vector<std::pair<double, Point>> samples;
        samples.reserve(11);
        for (double t = 0; t <= world_length; t += 0.2) {
            auto pt = path->evaluate(t);
            samples.emplace_back(t, pt.position());
        }

        for (const auto& [t, pos] : samples) {
            std::cout << "t = " << t << ":\n";
            print_point("  Position", pos);
        }

        // Check transition point
        const auto trans_pt = path->evaluate(0.5);
        const Point expected_trans(1, 0.75, 0);
        assert(approx_equal(trans_pt.position(), expected_trans));
        std::cout << "  Front to right transition test passed\n";
    }

    // Test 2: Complete circuit
    {
        std::cout << "\nTest 2: Complete circuit\n";
        auto front = test.cube_.get_surface("front");
        assert(front);

        // Start at center of front face and go right
        const Point world_start(0, 1, 0);
        const Vector world_dir(1, 0, 0);
        constexpr double world_length = 8.0;  // Enough to go around cube

        const auto [u_start, v_start] = CubeFaceTest::world_to_params(world_start);
        const auto start_params = front->evaluate(u_start, v_start);

        auto path = test.cube_.create_path(start_params, world_dir, world_length);

        // Check points at face transitions
        const std::vector<std::pair<double, Point>> check_points = {
            {1.0, Point(1, 0, 0)},    // Front to right
            {3.0, Point(0, -1, 0)},   // Right to back
            {5.0, Point(-1, 0, 0)},   // Back to left
            {7.0, Point(0, 1, 0)}     // Left to front
        };

        for (const auto& [t, expected] : check_points) {
            const auto pt = path->evaluate(t);
            assert(approx_equal(pt.position(), expected));
        }
        std::cout << "  Complete circuit test passed\n";
    }

    // Test 3: Performance
    {
        std::cout << "\nTest 3: Performance\n";
        auto front = test.cube_.get_surface("front");
        assert(front);

        const Point world_start(0.5, 1, 0);
        const Vector world_dir = Vector(1, -0.5, 0).normalize();
