};

SurfacePoint Surface::evaluate(double u, double v) const {
    validate_parameters(u, v);
    const GeometricProperties props = compute_properties(u, v);
    return SurfacePoint(
        const_cast<Surface*>(this),
        u, v,
        props.position,
        props.normal,
        props.du,
        props.dv
    );
}

std::unique_ptr<SurfacePath> Surface::create_path(
    const SurfacePoint& start,
    const Vector& direction,
    double length
) const {
    if (length <= 0) {
        throw std::invalid_argument("Path length must be positive");
    }
    if (direction.length_squared() < EPSILON) {
        throw std::invalid_argument("Direction vector cannot be zero");
    }

    auto path = std::make_unique<PathSegment>(
        std::shared_ptr<Surface>(const_cast<Surface*>(this), [](Surface*){})
    );

    // Project direction onto surface tangent plane
    const auto start_props = compute_properties(start.u(), start.v());
    Vector tangent_dir = direction - dot(direction, start_props.normal) * start_props.normal;
    if (tangent_dir.length_squared() < EPSILON) {
        throw std::runtime_error("Direction is perpendicular to surface");
    }
    tangent_dir = tangent_dir.normalize();

    // Try path solver first for surface transitions
    if (auto solver = get_path_solver()) {
        if (auto intersection = (*solver)(start.position(), direction, length)) {
            // Convert end point to parameter space
            const auto end_params = world_to_parameters(intersection->position);
            const auto start_params = std::make_pair(start.u(), start.v());

            path->add_point(0.0, start_params.first, start_params.second);

            // Sample points up to intersection
            for (int i = 1; i < PATH_POINTS; ++i) {
                const double alpha = static_cast<double>(i) / (PATH_POINTS - 1);
                const double t = alpha;  // Already normalized since intersection->t is the total length

                // Linear interpolation in parameter space
                const double u = start_params.first + (end_params.first - start_params.first) * alpha;
                const double v = start_params.second + (end_params.second - start_params.second) * alpha;

                path->add_point(t, u, v);
            }
            return path;
        }
    }

    // Diagnostic: Log input parameters
    std::cout << "\nPath Creation Diagnostics:\n"
              << "Start position: " << start.position().x << ", " 
              << start.position().y << ", " << start.position().z << "\n"
              << "Direction: " << tangent_dir.x << ", " 
              << tangent_dir.y << ", " << tangent_dir.z << "\n"
              << "Length: " << length << "\n"
              << "Start parameters: u=" << start.u() << " v=" << start.v() << "\n"
              << "Surface properties at start:\n"
              << "  du length: " << start_props.du.length() << "\n"
              << "  dv length: " << start_props.dv.length() << "\n"
              << "  du: " << start_props.du.x << ", " 
              << start_props.du.y << ", " << start_props.du.z << "\n"
              << "  dv: " << start_props.dv.x << ", " 
              << start_props.dv.y << ", " << start_props.dv.z << "\n";

