#include <shap/surface.hpp>
#include <shap/surfaces/basic_surfaces.hpp>
#include <cassert>
#include <cmath>
#include <iostream>
#include <iomanip>
#include <vector>
#include <chrono>
#include <functional>

namespace shap::test {

// Test utilities
namespace {
    constexpr double EPSILON = 1e-10;

    // Helper to check if two doubles are approximately equal
    [[nodiscard]] constexpr bool approx_equal(double a, double b, double epsilon = EPSILON) noexcept {
        return std::abs(a - b) <= epsilon;
    }

    // Helper to check if two points are approximately equal
    [[nodiscard]] bool approx_equal(const Point& a, const Point& b, double epsilon = EPSILON) noexcept {
        const bool result = approx_equal(a.x, b.x, epsilon) &&
                          approx_equal(a.y, b.y, epsilon) &&
                          approx_equal(a.z, b.z, epsilon);

        if (!result) {
            std::cout << "Point comparison failed:\n"
                     << "  Expected: (" << a.x << ", " << a.y << ", " << a.z << ")\n"
                     << "  Actual:   (" << b.x << ", " << b.y << ", " << b.z << ")\n"
                     << "  Diff:     (" 
                     << std::abs(a.x - b.x) << ", "
                     << std::abs(a.y - b.y) << ", "
                     << std::abs(a.z - b.z) << ")\n"
                     << "  Epsilon:  " << epsilon << "\n";
        }
        return result;
    }

    // Print a point for debugging
    void print_point(std::string_view label, const Point& p) {
        std::cout << label << ": ("
                 << std::fixed << std::setprecision(6)
                 << p.x << ", " << p.y << ", " << p.z << ")\n";
    }

    // Test fixture for cube face tests
    class CubeFaceTest {
    public:
        CubeFaceTest() : face_(create_front_face()) {}

        // Create front face of unit cube (y = 1)
        static std::shared_ptr<surfaces::SquareFace> create_front_face() {
            return std::make_shared<surfaces::SquareFace>(
                Point(-1, 1, -1),    // origin at top-left
                Vector(2, 0, 0),     // u: left to right
                Vector(0, 0, 2)      // v: top to bottom
            );
        }

        // Helper to convert world coordinates to parameters
        [[nodiscard]] static std::pair<double, double> world_to_params(const Point& world) noexcept {
            return {
                (world.x + 1) / 2,  // u = (x + 1)/2
                (world.z + 1) / 2   // v = (z + 1)/2
            };
        }

        std::shared_ptr<surfaces::SquareFace> face_;
    };

    // Test fixture for cube tests
    class CubeTest {
    public:
        CubeTest() : cube_(surfaces::create_cube(1.0)) {}
        SurfaceCollection cube_;
    };

    // Timing utility
    template<typename F>
    double time_operation(F&& func) {
        const auto start = std::chrono::high_resolution_clock::now();
        func();
        const auto end = std::chrono::high_resolution_clock::now();
        const auto duration = std::chrono::duration_cast<std::chrono::microseconds>(end - start);
        return static_cast<double>(duration.count()) / 1000.0;  // Convert to milliseconds
    }
}

// Test straight line paths on cube face
void test_cube_face_paths() {
    std::cout << "Testing cube face paths...\n";
    CubeFaceTest test;

    // Test 1: Path parallel to u axis
    {
        std::cout << "\nTest 1: Parallel path\n";

        // World space path: straight line from (-0.5, 1, 0) to (0.5, 1, 0)
        const Point world_start(-0.5, 1, 0);
        const Vector world_dir(1, 0, 0);  // Already normalized
        constexpr double world_length = 1.0;

        // Convert to parameter space
        const auto [u_start, v_start] = test.world_to_params(world_start);

        std::cout << "World space:\n";
        print_point("  Start", world_start);
        std::cout << "Parameter space:\n";
        std::cout << "  Start: (u=" << u_start << ", v=" << v_start << ")\n";

        // Create and evaluate path
        const auto start_params = test.face_->evaluate(u_start, v_start);
        print_point("Start params position", start_params.position());

        // Print surface scale information
        const auto props = test.face_->compute_properties(u_start, v_start);
        std::cout << "Surface scale factors:\n"
                 << "  du length: " << props.du.length() << "\n"
                 << "  dv length: " << props.dv.length() << "\n";

        auto path = test.face_->create_path(start_params, world_dir, world_length);

        // Sample points along path
        std::vector<std::pair<double, Point>> samples;
        samples.reserve(6);
        for (double t = 0; t <= 1.0; t += 0.2) {
            auto pt = path->evaluate(t);
            samples.emplace_back(t, pt.position());
        }

        for (const auto& [t, pos] : samples) {
            std::cout << "t = " << t << ":\n";
            print_point("  Position", pos);
        }

        // Check end point
        const auto end_pt = path->evaluate(1.0);
        const Point expected_end(0.5, 1, 0);
        assert(approx_equal(end_pt.position(), expected_end));
        std::cout << "  Parallel path test passed\n";
    }

    // Test 2: Diagonal path
    {
        std::cout << "\nTest 2: Diagonal path\n";

        // World space path: diagonal from (-0.5, 1, -0.5) to (0.5, 1, 0.5)
        const Point world_start(-0.5, 1, -0.5);
        const Vector world_dir = Vector(1, 0, 1).normalize();
        constexpr double world_length = std::sqrt(2);  // Length to reach (0.5, 1, 0.5)

        // Convert to parameter space
        const auto [u_start, v_start] = test.world_to_params(world_start);

        std::cout << "World space:\n";
        print_point("  Start", world_start);
        std::cout << "  Direction: (" << world_dir.x << ", " 
                 << world_dir.y << ", " << world_dir.z << ")\n";
        std::cout << "  Length: " << world_length << "\n";
        std::cout << "Parameter space:\n";
        std::cout << "  Start: (u=" << u_start << ", v=" << v_start << ")\n";

        // Create and evaluate path
        const auto start_params = test.face_->evaluate(u_start, v_start);
        print_point("Start params position", start_params.position());

        auto path = test.face_->create_path(start_params, world_dir, world_length);

        // Sample points along path
        std::vector<std::pair<double, Point>> samples;
        samples.reserve(11);
        for (double t = 0; t <= world_length; t += 0.2) {
            auto pt = path->evaluate(t);
            samples.emplace_back(t, pt.position());
        }

        for (const auto& [t, pos] : samples) {
            std::cout << "t = " << t << ":\n";
            print_point("  Position", pos);
        }

        // Check end point
        const auto end_pt = path->evaluate(world_length);
        const Point expected_end(0.5, 1, 0.5);
        assert(approx_equal(end_pt.position(), expected_end));
        std::cout << "  Diagonal path test passed\n";
    }

    // Test 3: Edge cases
    {
        std::cout << "\nTest 3: Edge cases\n";

        // Test zero length path
        {
            const Point start(-0.5, 1, 0);
            const Vector dir(1, 0, 0);
            const auto [u, v] = test.world_to_params(start);
            const auto start_params = test.face_->evaluate(u, v);

            bool caught_exception = false;
            try {
                [[maybe_unused]] auto path = test.face_->create_path(start_params, dir, 0.0);
            } catch (const std::invalid_argument&) {
                caught_exception = true;
            }
            assert(caught_exception);
            std::cout << "  Zero length path test passed\n";
        }

        // Test perpendicular direction
        {
            const Point start(-0.5, 1, 0);
            const Vector dir(0, 1, 0);  // Perpendicular to face
            const auto [u, v] = test.world_to_params(start);
            const auto start_params = test.face_->evaluate(u, v);

            bool caught_exception = false;
            try {
                [[maybe_unused]] auto path = test.face_->create_path(start_params, dir, 1.0);
            } catch (const std::runtime_error&) {
                caught_exception = true;
            }
            assert(caught_exception);
            std::cout << "  Perpendicular direction test passed\n";
        }
    }

    // Test 4: Performance
    {
        std::cout << "\nTest 4: Performance\n";

        const Point start(-0.5, 1, 0);
        const Vector dir(1, 0, 0);
        const auto [u, v] = test.world_to_params(start);
        const auto start_params = test.face_->evaluate(u, v);

        constexpr int NUM_PATHS = 1000;
        const double time_ms = time_operation([&]() {
            for (int i = 0; i < NUM_PATHS; ++i) {
                auto path = test.face_->create_path(start_params, dir, 1.0);
                for (double t = 0; t <= 1.0; t += 0.1) {
                    path->evaluate(t);
                }
            }
        });

        std::cout << "  Created and evaluated " << NUM_PATHS 
                 << " paths in " << time_ms << "ms\n"
                 << "  Average time per path: " << (time_ms / NUM_PATHS) << "ms\n";
    }
}

// Test path transitions between cube faces
void test_cube_face_transitions() {
    std::cout << "\nTesting cube face transitions...\n";
    CubeTest test;

    // Test 1: Front to right transition
    {
        std::cout << "\nTest 1: Front to right transition\n";
        auto front = test.cube_.get_surface("front");
        assert(front);

        // World space path: from (0.5, 1, 0) to (1.5, 0.5, 0)
        const Point world_start(0.5, 1, 0);
        const Vector world_dir = Vector(1, -0.5, 0).normalize();
        constexpr double world_length = std::sqrt(1.25);

        // Convert to parameter space for front face
        const auto [u_start, v_start] = CubeFaceTest::world_to_params(world_start);

        std::cout << "World space:\n";
        print_point("  Start", world_start);
        std::cout << "  Direction: (" << world_dir.x << ", " 
                 << world_dir.y << ", " << world_dir.z << ")\n";
        std::cout << "  Length: " << world_length << "\n";
        std::cout << "Parameter space:\n";
        std::cout << "  Start: (u=" << u_start << ", v=" << v_start << ")\n";

        // Create and evaluate path
        const auto start_params = front->evaluate(u_start, v_start);
        print_point("Start params position", start_params.position());

        auto path = test.cube_.create_path(start_params, world_dir, world_length);

        // Sample points along path
        std::vector<std::pair<double, Point>> samples;
        samples.reserve(11);
        for (double t = 0; t <= world_length; t += 0.2) {
            auto pt = path->evaluate(t);
            samples.emplace_back(t, pt.position());
        }

        for (const auto& [t, pos] : samples) {
            std::cout << "t = " << t << ":\n";
            print_point("  Position", pos);
        }

        // Check transition point
        const auto trans_pt = path->evaluate(0.5);
        const Point expected_trans(1, 0.75, 0);
        assert(approx_equal(trans_pt.position(), expected_trans));
        std::cout << "  Front to right transition test passed\n";
    }

    // Test 2: Complete circuit
    {
        std::cout << "\nTest 2: Complete circuit\n";
        auto front = test.cube_.get_surface("front");
        assert(front);

        // Start at center of front face and go right
        const Point world_start(0, 1, 0);
        const Vector world_dir(1, 0, 0);
        constexpr double world_length = 8.0;  // Enough to go around cube

        const auto [u_start, v_start] = CubeFaceTest::world_to_params(world_start);
        const auto start_params = front->evaluate(u_start, v_start);

        auto path = test.cube_.create_path(start_params, world_dir, world_length);

        // Check points at face transitions
        const std::vector<std::pair<double, Point>> check_points = {
            {1.0, Point(1, 0, 0)},    // Front to right
            {3.0, Point(0, -1, 0)},   // Right to back
            {5.0, Point(-1, 0, 0)},   // Back to left
            {7.0, Point(0, 1, 0)}     // Left to front
        };

        for (const auto& [t, expected] : check_points) {
            const auto pt = path->evaluate(t);
            assert(approx_equal(pt.position(), expected));
        }
        std::cout << "  Complete circuit test passed\n";
    }

    // Test 3: Performance
    {
        std::cout << "\nTest 3: Performance\n";
        auto front = test.cube_.get_surface("front");
        assert(front);

        const Point world_start(0.5, 1, 0);
        const Vector world_dir = Vector(1, -0.5, 0).normalize();
        constexpr double world_length = std::sqrt(1.25);

        const auto [u_start, v_start] = CubeFaceTest::world_to_params(world_start);
        const auto start_params = front->evaluate(u_start, v_start);

        constexpr int NUM_PATHS = 1000;
        const double time_ms = time_operation([&]() {
            for (int i = 0; i < NUM_PATHS; ++i) {
                auto path = test.cube_.create_path(start_params, world_dir, world_length);
                for (double t = 0; t <= world_length; t += 0.2) {
                    path->evaluate(t);
                }
            }
        });

        std::cout << "  Created and evaluated " << NUM_PATHS 
                 << " transition paths in " << time_ms << "ms\n"
                 << "  Average time per path: " << (time_ms / NUM_PATHS) << "ms\n";
    }
}

} // namespace shap::test

int main() {
    std::cout << "Running path tests...\n\n";

    try {
        shap::test::test_cube_face_paths();
        std::cout << "\n";

        shap::test::test_cube_face_transitions();
        std::cout << "\n";

        std::cout << "All tests passed!\n";
        return 0;
    }
    catch (const std::exception& e) {
        std::cerr << "Test failed: " << e.what() << "\n";
        return 1;
    }
}

#include <shap/surface.hpp>
#include <shap/surfaces/basic_surfaces.hpp>
#include <cassert>
#include <cmath>
#include <iostream>
#include <iomanip>

namespace shap::test {

namespace {
    constexpr double EPSILON = 1e-10;

    [[nodiscard]] constexpr bool approx_equal(double a, double b, double epsilon = EPSILON) noexcept {
        return std::abs(a - b) <= epsilon;
    }

    void print_point(std::string_view label, const Point& p) {
        std::cout << label << ": ("
                 << std::fixed << std::setprecision(6)
                 << p.x << ", " << p.y << ", " << p.z << ")\n";
    }
}

void test_path_length_invariants() {
    std::cout << "Testing path length invariants...\n\n";

    // Create a square face with known scale factors
    auto face = surfaces::create_square_face(
        Point(-1, 1, -1),    // origin at top-left
        Vector(2, 0, 0),     // du: scale factor of 2 in x
        Vector(0, 0, 2)      // dv: scale factor of 2 in z
    );

    std::cout << "Test 1: Parameter Space Distance\n";
    {
        // Create a path in u direction
        const Point start(-0.5, 1, 0);  // u = 0.25, v = 0.5
        const Vector dir(1, 0, 0);      // Unit vector in x
        const double length = 1.0;       // One unit in world space

        // Expected parameter space distance:
        // u = L/(2|du|) = 1.0/(2*2) = 0.25
        const double expected_param_delta = 0.25;

        // Create path
        const auto [u_start, v_start] = face->world_to_parameters(start);
        const auto start_point = face->evaluate(u_start, v_start);
        auto path = face->create_path(start_point, dir, length);

        // Check parameter space distance
        const auto end_pt = path->evaluate(1.0);
        const double actual_param_delta = end_pt.u() - start_point.u();

        std::cout << "Start parameters: u=" << start_point.u() << " v=" << start_point.v() << "\n"
                 << "End parameters: u=" << end_pt.u() << " v=" << end_pt.v() << "\n"
                 << "Expected parameter delta: " << expected_param_delta << "\n"
                 << "Actual parameter delta: " << actual_param_delta << "\n";

        assert(approx_equal(actual_param_delta, expected_param_delta));
    }

    std::cout << "\nTest 2: World Space Distance\n";
    {
        // Test points along path
        const Point start(-0.5, 1, 0);
        const Vector dir(1, 0, 0);
        const double length = 1.0;

        const auto [u_start, v_start] = face->world_to_parameters(start);
        const auto start_point = face->evaluate(u_start, v_start);
        auto path = face->create_path(start_point, dir, length);

        // Check distances at regular intervals
        for (double t = 0.0; t <= 1.0; t += 0.2) {
            const auto pt = path->evaluate(t);
            const auto pos = pt.position();
            const double actual_dist = (pos - start).length();
            const double expected_dist = t * length;

            std::cout << "t = " << t << ":\n";
            print_point("  Position", pos);
            std::cout << "  Expected distance: " << expected_dist << "\n"
                     << "  Actual distance: " << actual_dist << "\n"
                     << "  Error: " << std::abs(actual_dist - expected_dist) << "\n";

            assert(approx_equal(actual_dist, expected_dist));
        }
    }

    std::cout << "\nTest 3: Constant Speed\n";
    {
        // Test velocity at different points
        const Point start(-0.5, 1, 0);
        const Vector dir(1, 0, 0);
        const double length = 1.0;

        const auto [u_start, v_start] = face->world_to_parameters(start);
        const auto start_point = face->evaluate(u_start, v_start);
        auto path = face->create_path(start_point, dir, length);

        // Check velocity using finite differences
        const double dt = 0.1;
        for (double t = 0.0; t <= 1.0 - dt; t += 0.2) {
            const auto pt1 = path->evaluate(t);
            const auto pt2 = path->evaluate(t + dt);
            const double velocity = (pt2.position() - pt1.position()).length() / dt;

            std::cout << "t = " << t << ":\n"
                     << "  Velocity: " << velocity << "\n"
                     << "  Expected: " << length << "\n"
                     << "  Error: " << std::abs(velocity - length) << "\n";

            assert(approx_equal(velocity, length));
        }
    }

    std::cout << "\nAll path length invariant tests passed!\n";
}

} // namespace shap::test

int main() {
    try {
        shap::test::test_path_length_invariants();
        return 0;
    }
    catch (const std::exception& e) {
        std::cerr << "Test failed: " << e.what() << "\n";
        return 1;
    }
}

#include <shap/surface.hpp>
#include <shap/surfaces/basic_surfaces.hpp>
#include <cassert>
#include <cmath>
#include <iostream>
#include <iomanip>

namespace shap::test {

namespace {
    constexpr double EPSILON = 1e-10;

    // Helper to check if two doubles are approximately equal
    [[nodiscard]] constexpr bool approx_equal(double a, double b, double epsilon = EPSILON) noexcept {
        return std::abs(a - b) <= epsilon;
    }

    // Helper to check if two points are approximately equal
    [[nodiscard]] bool approx_equal(const Point& a, const Point& b, double epsilon = EPSILON) noexcept {
        const bool result = approx_equal(a.x, b.x, epsilon) &&
                          approx_equal(a.y, b.y, epsilon) &&
                          approx_equal(a.z, b.z, epsilon);

        if (!result) {
            std::cout << "Point comparison failed:\n"
                     << "  Expected: (" << a.x << ", " << a.y << ", " << a.z << ")\n"
                     << "  Actual:   (" << b.x << ", " << b.y << ", " << b.z << ")\n"
                     << "  Diff:     (" 
                     << std::abs(a.x - b.x) << ", "
                     << std::abs(a.y - b.y) << ", "
                     << std::abs(a.z - b.z) << ")\n"
                     << "  Epsilon:  " << epsilon << "\n";
        }
        return result;
    }

    // Print a point for debugging
    void print_point(std::string_view label, const Point& p) {
        std::cout << label << ": ("
                 << std::fixed << std::setprecision(6)
                 << p.x << ", " << p.y << ", " << p.z << ")\n";
    }
}

// Test space transformations
void test_space_transformations() {
    std::cout << "Testing space transformations...\n\n";

    // Create a square face with known scale factors
    auto face = surfaces::create_square_face(
        Point(-1, 1, -1),    // origin at top-left
        Vector(2, 0, 0),     // du: scale factor of 2 in x
        Vector(0, 0, 2)      // dv: scale factor of 2 in z
    );

    std::cout << "Test 1: Parameter to World Mapping\n";
    {
        // Test points in parameter space
        const std::vector<std::tuple<double, double, Point>> test_points = {
            {0.0, 0.0, Point(-1, 1, -1)},    // Origin
            {1.0, 0.0, Point(1, 1, -1)},     // u = 1
            {0.0, 1.0, Point(-1, 1, 1)},     // v = 1
            {0.5, 0.5, Point(0, 1, 0)}       // Center
        };

        for (const auto& [u, v, expected] : test_points) {
            const auto props = face->compute_properties(u, v);
            std::cout << "Parameter space: (u=" << u << ", v=" << v << ")\n";
            print_point("Expected world", expected);
            print_point("Actual world", props.position);
            assert(approx_equal(props.position, expected));
        }
    }

    std::cout << "\nTest 2: World to Parameter Mapping\n";
    {
        // Test points in world space
        const std::vector<std::tuple<Point, double, double>> test_points = {
            {Point(-1, 1, -1), 0.0, 0.0},    // Origin
            {Point(1, 1, -1), 1.0, 0.0},     // u = 1
            {Point(-1, 1, 1), 0.0, 1.0},     // v = 1
            {Point(0, 1, 0), 0.5, 0.5}       // Center
        };

        for (const auto& [world, expected_u, expected_v] : test_points) {
            const auto [u, v] = face->world_to_parameters(world);
            std::cout << "World space: ";
            print_point("", world);
            std::cout << "Expected parameters: (u=" << expected_u << ", v=" << expected_v << ")\n"
                     << "Actual parameters: (u=" << u << ", v=" << v << ")\n";
            assert(approx_equal(u, expected_u) && approx_equal(v, expected_v));
        }
    }

    std::cout << "\nTest 3: Path Length Preservation\n";
    {
        // Create paths with different scales and directions
        const std::vector<std::tuple<Point, Vector, double, Point>> test_paths = {
            // Start, Direction, Length, Expected End
            {Point(-0.5, 1, 0), Vector(1, 0, 0), 1.0, Point(0.5, 1, 0)},     // X direction
            {Point(0, 1, -0.5), Vector(0, 0, 1), 1.0, Point(0, 1, 0.5)},     // Z direction
            {Point(-0.5, 1, -0.5), Vector(1, 0, 1).normalize(), std::sqrt(2.0), Point(0.5, 1, 0.5)} // Diagonal
        };

        for (const auto& [start, dir, length, expected_end] : test_paths) {
            std::cout << "Path test:\n";
            print_point("Start", start);
            std::cout << "Direction: (" << dir.x << ", " << dir.y << ", " << dir.z << ")\n"
                     << "Length: " << length << "\n";
            print_point("Expected end", expected_end);

            // Create path
            const auto [u_start, v_start] = face->world_to_parameters(start);
            const auto start_point = face->evaluate(u_start, v_start);
            auto path = face->create_path(start_point, dir, length);

            // Check distances along path
            for (double t = 0.0; t <= 1.0; t += 0.2) {
                const auto pt = path->evaluate(t);
                const auto pos = pt.position();
                const double actual_dist = (pos - start).length();
                const double expected_dist = t * length;

                std::cout << "t = " << t << ":\n";
                print_point("  Position", pos);
                std::cout << "  Expected distance: " << expected_dist << "\n"
                         << "  Actual distance: " << actual_dist << "\n";
                assert(approx_equal(actual_dist, expected_dist));
            }

            // Check end point specifically
            const auto end_pt = path->evaluate(1.0);
            print_point("Actual end", end_pt.position());
            assert(approx_equal(end_pt.position(), expected_end));
        }
    }

    std::cout << "\nAll space transformation tests passed!\n";
}

} // namespace shap::test

int main() {
    try {
        shap::test::test_space_transformations();
        return 0;
    }
    catch (const std::exception& e) {
        std::cerr << "Test failed: " << e.what() << "\n";
        return 1;
    }
}
