#pragma once
#include "types.hpp"
#include "point.hpp"
#include <functional>
#include <memory>
#include <optional>
#include <utility>
#include <stdexcept>

namespace shap {

class SurfacePoint;
class SurfacePath;

// Function types for surface creation
using PositionFunction = std::function<Point(const ParameterCoordinate&)>;
using DerivativeFunction = std::function<Point(const ParameterCoordinate&)>;
using CurvatureFunction = std::function<double(const ParameterCoordinate&)>;

// Path solver returns intersection with surface boundary
struct PathIntersection {
    double t;                // Distance to intersection in world space
    Point position;          // World space intersection point
    ParamIndex param;        // Which parameter (u/v) hit boundary
    ParamBound bound;        // Which bound (0/1) was hit
    double edge_parameter;   // Parameter along the edge [0,1]

    // Constructor with validation
    PathIntersection(
        double t_,
        Point position_,
        ParamIndex param_,
        ParamBound bound_,
        double edge_parameter_
    ) : t(t_)
      , position(std::move(position_))
      , param(param_)
      , bound(bound_)
      , edge_parameter(edge_parameter_) {
        if (t_ < 0) {
            throw std::invalid_argument("Intersection distance must be non-negative");
        }
        if (edge_parameter_ < 0 || edge_parameter_ > 1) {
            throw std::invalid_argument("Edge parameter must be in [0,1]");
        }
    }
};

using PathSolver = std::function<std::optional<PathIntersection>(
    const Point& world_start,
    const Vector& world_direction,
    double max_world_distance
)>;

// Geometric properties at a surface point
struct GeometricProperties {
    Point world_position;                    // Position in world space
    Vector world_normal;                     // Surface normal in world space
    Vector world_du;                         // First derivative in u direction
    Vector world_dv;                         // First derivative in v direction
    std::optional<Point> world_duu;          // Second derivative in u direction
    std::optional<Point> world_duv;          // Mixed second derivative
    std::optional<Point> world_dvv;          // Second derivative in v direction
    std::optional<double> gaussian_curvature;
    std::optional<double> mean_curvature;
    std::optional<std::pair<double, double>> principal_curvatures;

    // Basic constructor with just position and derivatives
    GeometricProperties(
        Point pos,
        Vector n,
        Vector du_vec,
        Vector dv_vec
    ) noexcept
        : world_position(std::move(pos))
        , world_normal(std::move(n))
        , world_du(std::move(du_vec))
        , world_dv(std::move(dv_vec)) {}

    // Full constructor with all properties
    GeometricProperties(
        Point pos,
        Vector n,
        Vector du_vec,
        Vector dv_vec,
        Point duu_point,
        Point duv_point,
        Point dvv_point,
        double gaussian,
        double mean,
        std::pair<double, double> principal
    ) noexcept
        : world_position(std::move(pos))
        , world_normal(std::move(n))
        , world_du(std::move(du_vec))
        , world_dv(std::move(dv_vec))
        , world_duu(std::move(duu_point))
        , world_duv(std::move(duv_point))
        , world_dvv(std::move(dvv_point))
        , gaussian_curvature(gaussian)
        , mean_curvature(mean)
        , principal_curvatures(std::move(principal)) {}
};

class Surface {
public:
    virtual ~Surface() = default;

    // Prevent copying
    Surface(const Surface&) = delete;
    Surface& operator=(const Surface&) = delete;

    // Allow moving
    Surface(Surface&&) noexcept = default;
    Surface& operator=(Surface&&) noexcept = default;

protected:
    Surface() = default;

public:
    // Evaluate surface at parameter space point
    [[nodiscard]] SurfacePoint evaluate(const ParameterCoordinate& param) const;

    /**
     * Create a path on the surface starting from a point in a given direction.
     * 
     * @param start Starting point on the surface
     * @param world_direction Desired world-space direction (will be projected onto surface)
     * @param world_length Desired path length in world space units
     * @throws std::invalid_argument if preconditions are not met
     * @return Unique pointer to path object representing the curve
     */
    [[nodiscard]] virtual std::unique_ptr<SurfacePath> create_path(
        const SurfacePoint& start,
        const Vector& world_direction,
        double world_length
    ) const;

    // Get geometric properties at parameter space point
    [[nodiscard]] virtual GeometricProperties compute_properties(
        const ParameterCoordinate& param
    ) const = 0;

    // Get path solver if available
    [[nodiscard]] virtual std::optional<PathSolver> get_path_solver() const noexcept {
        return std::nullopt;
    }

    // Get surface type
    [[nodiscard]] virtual SurfaceType surface_type() const noexcept {
        return SurfaceType::Generic;
    }

    /**
     * Convert a world space position to parameter space coordinates.
     *
     * @param world_pos World space position to convert
     * @throws std::invalid_argument if pos is too far from surface
     * @return Parameter space coordinates
     */
    [[nodiscard]] virtual ParameterCoordinate world_to_parameter_space(
        const Point& world_pos
    ) const = 0;

    /**
     * Convert world space direction to parameter space velocity.
     * Accounts for surface metric tensor in the conversion.
     *
     * @param world_direction Direction vector in world space
     * @param world_du First derivative in u direction
     * @param world_dv First derivative in v direction
     * @return Velocity vector in parameter space
     */
    [[nodiscard]] ParameterVelocity world_to_parameter_velocity(
        const Vector& world_direction,
        const Point& world_du,
        const Point& world_dv
    ) const noexcept;

    /**
     * Get scale factors for converting between parameter and world space.
     * These represent how much a unit step in parameter space maps to in world space.
     *
     * @param param Parameter space point to compute scale factors at
     * @return Pair of scale factors (du_scale, dv_scale)
     */
    [[nodiscard]] std::pair<double, double> get_scale_factors(
        const ParameterCoordinate& param
    ) const {
        const auto props = compute_properties(param);
        return {props.world_du.length(), props.world_dv.length()};
    }

protected:
    // Validate parameter values are in [0,1]
    static void validate_parameters([[maybe_unused]] const ParameterCoordinate& param) {
        // ParameterCoordinate constructor handles validation
    }

public:
    // Factory methods
    [[nodiscard]] static std::shared_ptr<Surface> create(
        PositionFunction position_func,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Generic
    );

    [[nodiscard]] static std::shared_ptr<Surface> create_with_derivatives(
        PositionFunction position_func,
        DerivativeFunction du_func,
        DerivativeFunction dv_func,
        std::optional<CurvatureFunction> gaussian_curv = std::nullopt,
        std::optional<CurvatureFunction> mean_curv = std::nullopt,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Smooth
    );

    [[nodiscard]] static std::shared_ptr<Surface> create_with_all_derivatives(
        PositionFunction position_func,
        DerivativeFunction du_func,
        DerivativeFunction dv_func,
        DerivativeFunction duu_func,
        DerivativeFunction duv_func,
        DerivativeFunction dvv_func,
        std::optional<CurvatureFunction> gaussian_curv = std::nullopt,
        std::optional<CurvatureFunction> mean_curv = std::nullopt,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Smooth
