#include "shap/surface.hpp"
#include "shap/surface_point.hpp"
#include "shap/path.hpp"
#include <limits>
#include <cmath>
#include <array>
#include <iostream>

namespace shap {

namespace {
    constexpr double EPSILON = 1e-10;
    constexpr int GRID_SIZE = 10;
    constexpr int MAX_ITERATIONS = 20;
    constexpr double GRADIENT_STEP = 0.01;
    constexpr int PATH_POINTS = 10;

    // Adaptive step size for numerical derivatives
    [[nodiscard]] constexpr double compute_step_size(double x) noexcept {
        const double eps = std::numeric_limits<double>::epsilon();
        return std::cbrt(eps) * (1.0 + std::abs(x));
    }

    // Helper to compute normal from derivatives
    [[nodiscard]] Vector compute_normal(const Point& du, const Point& dv) noexcept {
        return cross(du, dv).normalize();
    }

    // Helper to compute curvature coefficients
    struct CurvatureCoefficients {
        double E, F, G;  // First fundamental form
        double L, M, N;  // Second fundamental form
        double det;      // EG - F

        [[nodiscard]] static CurvatureCoefficients compute(
            const Point& du,
            const Point& dv,
            const Point& duu,
            const Point& duv,
            const Point& dvv,
            const Vector& normal
        ) noexcept {
            CurvatureCoefficients coeff;
            coeff.E = dot(du, du);
            coeff.F = dot(du, dv);
            coeff.G = dot(dv, dv);
            coeff.L = dot(duu, normal);
            coeff.M = dot(duv, normal);
            coeff.N = dot(dvv, normal);
            coeff.det = coeff.E * coeff.G - coeff.F * coeff.F;
            return coeff;
        }

        [[nodiscard]] std::optional<double> gaussian_curvature() const noexcept {
            if (std::abs(det) <= EPSILON) return std::nullopt;
            return (L * N - M * M) / det;
        }

        [[nodiscard]] std::optional<double> mean_curvature() const noexcept {
            if (std::abs(det) <= EPSILON) return std::nullopt;
            return (E * N - 2.0 * F * M + G * L) / (2.0 * det);
        }
    };
} // anonymous namespace

class FunctionSurface final : public Surface {
public:
    FunctionSurface(
        PositionFunction pos,
        std::optional<DerivativeFunction> du = std::nullopt,
        std::optional<DerivativeFunction> dv = std::nullopt,
        std::optional<DerivativeFunction> duu = std::nullopt,
        std::optional<DerivativeFunction> duv = std::nullopt,
        std::optional<DerivativeFunction> dvv = std::nullopt,
        std::optional<CurvatureFunction> gaussian = std::nullopt,
        std::optional<CurvatureFunction> mean = std::nullopt,
        std::optional<PathSolver> path_solver = std::nullopt,
        SurfaceType type = SurfaceType::Smooth
    ) noexcept
        : position_func_(std::move(pos))
        , du_func_(std::move(du))
        , dv_func_(std::move(dv))
        , duu_func_(std::move(duu))
        , duv_func_(std::move(duv))
        , dvv_func_(std::move(dvv))
        , gaussian_curv_func_(std::move(gaussian))
        , mean_curv_func_(std::move(mean))
        , path_solver_(std::move(path_solver))
        , type_(type) {}

    [[nodiscard]] GeometricProperties compute_properties(
        const ParameterCoordinate& param
    ) const override {
        Point du, dv;

        // Compute first derivatives
        if (du_func_ && dv_func_) {
            du = (*du_func_)(param);
            dv = (*dv_func_)(param);
        } else {
            // Adaptive step size numerical derivatives
            const double hu = compute_step_size(param.u());
            const double hv = compute_step_size(param.v());

            const auto u_plus = ParameterCoordinate(param.u() + hu, param.v());
            const auto u_minus = ParameterCoordinate(param.u() - hu, param.v());
            const auto v_plus = ParameterCoordinate(param.u(), param.v() + hv);
            const auto v_minus = ParameterCoordinate(param.u(), param.v() - hv);

            du = (position_func_(u_plus) - position_func_(u_minus)) * (0.5 / hu);
            dv = (position_func_(v_plus) - position_func_(v_minus)) * (0.5 / hv);
        }

        const Vector normal = compute_normal(du, dv);
        const Point position = position_func_(param);

        // Create properties with first derivatives
        GeometricProperties props(
            std::move(position),
            normal,
            std::move(du),
            std::move(dv)
        );

        // Compute second derivatives if needed for smooth surfaces
        if (type_ == SurfaceType::Smooth) {
            Point duu, duv, dvv;

            if (duu_func_) {
                duu = (*duu_func_)(param);
            } else {
                const double hu = compute_step_size(param.u());
                const auto u_plus = ParameterCoordinate(param.u() + hu, param.v());
                const auto u_minus = ParameterCoordinate(param.u() - hu, param.v());
                duu = (position_func_(u_plus) - 2.0 * props.world_position +
                      position_func_(u_minus)) * (1.0 / (hu * hu));
            }

            if (duv_func_) {
                duv = (*duv_func_)(param);
            } else {
                const double hu = compute_step_size(param.u());
                const double hv = compute_step_size(param.v());
                const auto uv_plus = ParameterCoordinate(param.u() + hu, param.v() + hv);
                const auto uv_minus_u = ParameterCoordinate(param.u() + hu, param.v() - hv);
                const auto uv_minus_v = ParameterCoordinate(param.u() - hu, param.v() + hv);
                const auto uv_minus = ParameterCoordinate(param.u() - hu, param.v() - hv);
                duv = ((position_func_(uv_plus) - position_func_(uv_minus_u)) -
                      (position_func_(uv_minus_v) - position_func_(uv_minus))) * 
                     (0.25 / (hu * hv));
            }

            if (dvv_func_) {
                dvv = (*dvv_func_)(param);
            } else {
                const double hv = compute_step_size(param.v());
                const auto v_plus = ParameterCoordinate(param.u(), param.v() + hv);
                const auto v_minus = ParameterCoordinate(param.u(), param.v() - hv);
                dvv = (position_func_(v_plus) - 2.0 * props.world_position +
                      position_func_(v_minus)) * (1.0 / (hv * hv));
            }

            // Compute curvature
            const auto coeffs = CurvatureCoefficients::compute(
                props.world_du, props.world_dv, duu, duv, dvv, props.world_normal);

            if (gaussian_curv_func_) {
                props.gaussian_curvature = (*gaussian_curv_func_)(param);
            } else if (auto k = coeffs.gaussian_curvature()) {
                props.gaussian_curvature = *k;
            }

            if (mean_curv_func_) {
                props.mean_curvature = (*mean_curv_func_)(param);
            } else if (auto h = coeffs.mean_curvature()) {
                props.mean_curvature = *h;
            }

            // Compute principal curvatures if possible
            if (props.gaussian_curvature && props.mean_curvature) {
                const double H = *props.mean_curvature;
                const double K = *props.gaussian_curvature;
                const double disc = H*H - K;

                if (disc >= 0) {
                    const double sqrt_disc = std::sqrt(disc);
                    if (H >= 0) {
                        const double k1 = H + sqrt_disc;
                        const double k2 = K / k1;  // More stable than H - sqrt_disc
                        props.principal_curvatures = std::make_pair(k1, k2);
                    } else {
                        const double k2 = H - sqrt_disc;
                        const double k1 = K / k2;  // More stable than H + sqrt_disc
                        props.principal_curvatures = std::make_pair(k1, k2);
                    }
                }
            }
        }

        return props;
    }

    [[nodiscard]] std::optional<PathSolver> get_path_solver() const noexcept override {
        return path_solver_;
    }

    [[nodiscard]] SurfaceType surface_type() const noexcept override {
        return type_;
    }

    [[nodiscard]] ParameterCoordinate world_to_parameter_space(
        const Point& world_pos
    ) const override {
        // Grid search for initial guess
        double best_u = 0, best_v = 0;
        double min_dist = std::numeric_limits<double>::max();

        for (int i = 0; i <= GRID_SIZE; ++i) {
            const double u = static_cast<double>(i) / GRID_SIZE;
            for (int j = 0; j <= GRID_SIZE; ++j) {
                const double v = static_cast<double>(j) / GRID_SIZE;
                const auto param = ParameterCoordinate(u, v);
                const Point surface_pt = position_func_(param);
                const double dist = (surface_pt - world_pos).length_squared();
                if (dist < min_dist) {
                    min_dist = dist;
                    best_u = u;
                    best_v = v;
                }
            }
        }

        // Gradient descent refinement
        for (int iter = 0; iter < MAX_ITERATIONS; ++iter) {
            const auto param = ParameterCoordinate(best_u, best_v);
            const Point curr_pos = position_func_(param);
            const Vector diff = world_pos - curr_pos;
            if (diff.length_squared() < EPSILON) break;

            // Compute numerical derivatives
            const double hu = compute_step_size(best_u);
            const double hv = compute_step_size(best_v);

            const auto u_plus = ParameterCoordinate(best_u + hu, best_v);
            const auto v_plus = ParameterCoordinate(best_u, best_v + hv);

            const Point du = (position_func_(u_plus) - curr_pos) * (1.0/hu);
            const Point dv = (position_func_(v_plus) - curr_pos) * (1.0/hv);

            // Update parameters
            best_u = std::clamp(best_u + GRADIENT_STEP * dot(diff, du), 0.0, 1.0);
            best_v = std::clamp(best_v + GRADIENT_STEP * dot(diff, dv), 0.0, 1.0);
        }

        return ParameterCoordinate(best_u, best_v);
    }

private:
    PositionFunction position_func_;
    std::optional<DerivativeFunction> du_func_;
    std::optional<DerivativeFunction> dv_func_;
    std::optional<DerivativeFunction> duu_func_;
    std::optional<DerivativeFunction> duv_func_;
    std::optional<DerivativeFunction> dvv_func_;
    std::optional<CurvatureFunction> gaussian_curv_func_;
    std::optional<CurvatureFunction> mean_curv_func_;
    std::optional<PathSolver> path_solver_;
    SurfaceType type_;
};

SurfacePoint Surface::evaluate(const ParameterCoordinate& param) const {
    const GeometricProperties props = compute_properties(param);
    return SurfacePoint(
        const_cast<Surface*>(this),
        param,
        props.world_position,
        props.world_normal,
        props.world_du,
        props.world_dv
    );
}

ParameterVelocity Surface::world_to_parameter_velocity(
    const Vector& world_direction,
    const Point& world_du,
    const Point& world_dv
) const noexcept {
    const double du_du = dot(world_du, world_du);
    const double du_dv = dot(world_du, world_dv);
    const double dv_dv = dot(world_dv, world_dv);
    const double det = du_du * dv_dv - du_dv * du_dv;

    // Handle near-singular cases
    if (std::abs(det) < EPSILON) {
        // Project onto dominant direction
        if (du_du > dv_dv) {
            return ParameterVelocity(dot(world_direction, world_du.normalize()), 0.0);
        } else {
            return ParameterVelocity(0.0, dot(world_direction, world_dv.normalize()));
        }
    }

    const double inv_det = 1.0 / det;
    const double dir_du = dot(world_direction, world_du);
    const double dir_dv = dot(world_direction, world_dv);

    return ParameterVelocity(
        (dir_du * dv_dv - dir_dv * du_dv) * inv_det,
        (dir_dv * du_du - dir_du * du_dv) * inv_det
    );
}

std::unique_ptr<SurfacePath> Surface::create_path(
    const SurfacePoint& start,
    const Vector& world_direction,
    double world_length
) const {
    if (world_length <= 0) {
        throw std::invalid_argument("Path length must be positive");
    }
    if (world_direction.length_squared() < EPSILON) {
        throw std::invalid_argument("Direction vector cannot be zero");
    }

    auto path = std::make_unique<PathSegment>(
        std::shared_ptr<Surface>(const_cast<Surface*>(this), [](Surface*){})
    );

    // Project direction onto surface tangent plane
    const auto start_props = compute_properties(start.parameter_coordinates());
    Vector tangent_dir = world_direction - dot(world_direction, start_props.world_normal) * start_props.world_normal;
    if (tangent_dir.length_squared() < EPSILON) {
        throw std::runtime_error("Direction is perpendicular to surface");
    }
    tangent_dir = tangent_dir.normalize();

    // Try path solver first for surface transitions
    if (auto solver = get_path_solver()) {
        if (auto intersection = (*solver)(start.world_position(), world_direction, world_length)) {
            // Convert end point to parameter space
            const auto end_params = world_to_parameter_space(intersection->position);
            const auto& start_params = start.parameter_coordinates();

            path->add_point(0.0, start_params.u(), start_params.v());

            // Sample points up to intersection
            for (int i = 1; i < PATH_POINTS; ++i) {
                const double alpha = static_cast<double>(i) / (PATH_POINTS - 1);
                const double t = alpha;  // Already normalized since intersection->t is the total length

                // Linear interpolation in parameter space
                const double u = start_params.u() + (end_params.u() - start_params.u()) * alpha;
                const double v = start_params.v() + (end_params.v() - start_params.v()) * alpha;

                path->add_point(t, u, v);
            }
            return path;
        }
    }

    // Diagnostic: Log input parameters
    std::cout << "\nPath Creation Diagnostics:\n"
              << "Start position: " << start.world_position().x << ", " 
              << start.world_position().y << ", " << start.world_position().z << "\n"
              << "Direction: " << tangent_dir.x << ", " 
              << tangent_dir.y << ", " << tangent_dir.z << "\n"
              << "Length: " << world_length << "\n"
              << "Start parameters: u=" << start.parameter_coordinates().u() 
              << " v=" << start.parameter_coordinates().v() << "\n"
              << "Surface properties at start:\n"
              << "  du length: " << start_props.world_du.length() << "\n"
              << "  dv length: " << start_props.world_dv.length() << "\n"
              << "  du: " << start_props.world_du.x << ", " 
              << start_props.world_du.y << ", " << start_props.world_du.z << "\n"
              << "  dv: " << start_props.world_dv.x << ", " 
              << start_props.world_dv.y << ", " << start_props.world_dv.z << "\n";

    // Convert direction to parameter space and scale by surface metric
    const auto param_vel = world_to_parameter_velocity(
        tangent_dir, start_props.world_du, start_props.world_dv);

    // Compute metric scale factors
    const auto [du_scale, dv_scale] = get_scale_factors(start.parameter_coordinates());

    // Parameter velocity already includes metric scaling from world_to_parameter_velocity
    const auto& start_params = start.parameter_coordinates();
    const auto end_params = ParameterCoordinate(
        start_params.u() + param_vel.du() * world_length,
        start_params.v() + param_vel.dv() * world_length
    );

    // Diagnostic: Log end point computation
    const auto end_point = evaluate(end_params);
    std::cout << "End parameters: u=" << end_params.u() 
              << " v=" << end_params.v() << "\n"
              << "End position (world): " << end_point.world_position().x << ", " 
              << end_point.world_position().y << ", " << end_point.world_position().z << "\n"
              << "Parameter space delta: du=" 
              << (end_params.u() - start_params.u())
              << " dv=" << (end_params.v() - start_params.v()) << "\n"
              << "Parameter derivatives (includes metric): du=" << param_vel.du() 
              << " dv=" << param_vel.dv() << "\n"
              << "Expected world distance: " << world_length << "\n";

    path->add_point(0.0, start_params.u(), start_params.v());

    // Sample points along parameter space line
    for (int i = 1; i <= PATH_POINTS; ++i) {  // Changed to <= to ensure we hit end point
        const double alpha = static_cast<double>(i) / PATH_POINTS;

        // Linear interpolation in parameter space
        const double u = start_params.u() + (end_params.u() - start_params.u()) * alpha;
        const double v = start_params.v() + (end_params.v() - start_params.v()) * alpha;

        // Use normalized t in [0,1] while maintaining requested length
        path->add_point(alpha, u, v);
    }

    return path;
}

std::shared_ptr<Surface> Surface::create(
    PositionFunction position_func,
    std::optional<PathSolver> path_solver,
    SurfaceType type
) {
    if (!position_func) {
        throw std::invalid_argument("Position function cannot be null");
    }
    return std::make_shared<FunctionSurface>(
        std::move(position_func),
        std::nullopt, std::nullopt,
