    std::cout << "\nBefore world_to_parameter_velocity:\n"
              << "Tangent direction: " << tangent_dir.x << ", "
              << tangent_dir.y << ", " << tangent_dir.z << "\n"
              << "Surface du: " << start_props.world_du.x << ", "
              << start_props.world_du.y << ", " << start_props.world_du.z
              << " (length=" << start_props.world_du.length() << ")\n"
              << "Surface dv: " << start_props.world_dv.x << ", "
              << start_props.world_dv.y << ", " << start_props.world_dv.z
              << " (length=" << start_props.world_dv.length() << ")\n";

