#include "shap/path.hpp"
#include <stdexcept>
#include <cmath>
#include <algorithm>
#include <array>
#include <iostream>

namespace shap {

namespace {
    // Constants for numerical integration
    constexpr int GEODESIC_STEPS = 50;
    constexpr double GEODESIC_DT = 1.0 / GEODESIC_STEPS;
    constexpr double CURVATURE_EPSILON = 1e-10;
    constexpr int BASE_TRANSITION_POINTS = 10;

    // Helper for RK4 integration
    struct RK4State {
        double u, v;      // Position
        double up, vp;    // Velocity
    };
}

GeodesicCurve::GeodesicCurve(
    std::shared_ptr<Surface> surface,
    const SurfacePoint& start,
    const SurfacePoint& end
) : surface_(std::move(surface)) {
    points_.reserve(GEODESIC_STEPS + 1);  // Pre-allocate for efficiency

    if (!surface_) {
        throw std::invalid_argument("Surface pointer cannot be null");
    }

    switch (surface_->surface_type()) {
        case SurfaceType::Smooth:
            compute_smooth_geodesic(start, end);
            break;
        case SurfaceType::Developable:
            compute_developable_geodesic(start, end);
            break;
        default:
            // Use piecewise linear approximation for non-smooth surfaces
            points_.push_back(start);
            points_.push_back(end);
    }
}

void GeodesicCurve::compute_smooth_geodesic(
    const SurfacePoint& start,
    const SurfacePoint& end
) {
    // Initial conditions
    const auto start_params = start.parameter_coordinates();
    const auto end_params = end.parameter_coordinates();
    RK4State state{
        start_params.u(), start_params.v(),                    // Position
        end_params.u() - start_params.u(),                     // Initial velocity u
        end_params.v() - start_params.v()                      // Initial velocity v
    };

    // Normalize initial velocity
    const double len = std::sqrt(state.up*state.up + state.vp*state.vp);
    if (len > CURVATURE_EPSILON) {
        state.up /= len;
        state.vp /= len;
    }

    // Pre-allocate vectors
    std::vector<double> u_vals;
    std::vector<double> v_vals;
    u_vals.reserve(GEODESIC_STEPS + 1);
    v_vals.reserve(GEODESIC_STEPS + 1);
    u_vals.push_back(state.u);
    v_vals.push_back(state.v);

    // RK4 integration
    for (int i = 0; i < GEODESIC_STEPS; ++i) {
        const auto param = ParameterCoordinate(state.u, state.v);
        auto props = surface_->compute_properties(param);

        // Compute Christoffel symbols for geodesic equation
        const double E = dot(props.world_du, props.world_du);
        const double F = dot(props.world_du, props.world_dv);
        const double G = dot(props.world_dv, props.world_dv);

        // Second derivatives (with zero fallback)
        const Point duu = props.world_duu.value_or(Point());
        const Point duv = props.world_duv.value_or(Point());
        const Point dvv = props.world_dvv.value_or(Point());

        // Normal components
        const double L = dot(duu, props.world_normal);
        const double M = dot(duv, props.world_normal);
        const double N = dot(dvv, props.world_normal);

        const double det = E*G - F*F;
        if (std::abs(det) < CURVATURE_EPSILON) {
            continue;
        }

        const double inv_det = 1.0 / det;
        const std::array<double, 6> symbols{
            (G*L - F*M) * inv_det,  // Γ¹₁₁
            (G*M - F*N) * inv_det,  // Γ¹₁₂
            (-F*L + E*M) * inv_det, // Γ²₁₁
            (-F*M + E*N) * inv_det, // Γ²₁₂
            (G*M - F*N) * inv_det,  // Γ¹₂₂ = Γ¹₁₂
            (-F*M + E*N) * inv_det  // Γ²₂₂ = Γ²₁₂
        };

        // Geodesic equation solver
        auto compute_deriv = [&symbols](const RK4State& s) noexcept {
            // Compute accelerations using geodesic equation
            const double acc_u = -(symbols[0]*s.up*s.up +
                                 2*symbols[1]*s.up*s.vp +
                                 symbols[4]*s.vp*s.vp);
            const double acc_v = -(symbols[2]*s.up*s.up +
                                 2*symbols[3]*s.up*s.vp +
                                 symbols[5]*s.vp*s.vp);
            return std::make_pair(acc_u, acc_v);
        };

        // RK4 steps
        const auto [k1u, k1v] = compute_deriv(state);

        RK4State k2_state{
            state.u + 0.5*GEODESIC_DT*state.up,
            state.v + 0.5*GEODESIC_DT*state.vp,
            state.up + 0.5*GEODESIC_DT*k1u,
            state.vp + 0.5*GEODESIC_DT*k1v
        };
        const auto [k2u, k2v] = compute_deriv(k2_state);

        RK4State k3_state{
            state.u + 0.5*GEODESIC_DT*state.up,
            state.v + 0.5*GEODESIC_DT*state.vp,
            state.up + 0.5*GEODESIC_DT*k2u,
            state.vp + 0.5*GEODESIC_DT*k2v
        };
        const auto [k3u, k3v] = compute_deriv(k3_state);

        RK4State k4_state{
            state.u + GEODESIC_DT*state.up,
            state.v + GEODESIC_DT*state.vp,
            state.up + GEODESIC_DT*k3u,
            state.vp + GEODESIC_DT*k3v
        };
        const auto [k4u, k4v] = compute_deriv(k4_state);

        // Update state
        state.up += (GEODESIC_DT/6) * (k1u + 2*k2u + 2*k3u + k4u);
        state.vp += (GEODESIC_DT/6) * (k1v + 2*k2v + 2*k3v + k4v);
        state.u += GEODESIC_DT * state.up;
        state.v += GEODESIC_DT * state.vp;

        u_vals.push_back(state.u);
        v_vals.push_back(state.v);
    }

    // Create points along geodesic
    points_.clear();
    points_.reserve(u_vals.size());
    for (size_t i = 0; i < u_vals.size(); ++i) {
        const auto param = ParameterCoordinate(u_vals[i], v_vals[i]);
        points_.push_back(surface_->evaluate(param));
    }
}

void GeodesicCurve::compute_developable_geodesic(
    const SurfacePoint& start,
    const SurfacePoint& end
) {
    constexpr int steps = 20;
    points_.clear();
    points_.reserve(steps + 1);

    const auto start_params = start.parameter_coordinates();
    const auto end_params = end.parameter_coordinates();
    const double du = end_params.u() - start_params.u();
    const double dv = end_params.v() - start_params.v();

    for (int i = 0; i <= steps; ++i) {
        const double t = static_cast<double>(i) / steps;
        const double u = start_params.u() + t * du;
        const double v = start_params.v() + t * dv;
        const auto param = ParameterCoordinate(u, v);
        points_.push_back(surface_->evaluate(param));
    }
}

SurfacePoint GeodesicCurve::evaluate(double t) const {
    validate_parameter(t);

    if (points_.empty()) {
        throw std::runtime_error("Geodesic curve has no points");
    }

    // Find segment containing t
    const auto num_segments = static_cast<double>(points_.size() - 1);
    const double scaled_t = t * num_segments;
    const size_t i = std::min(static_cast<size_t>(scaled_t), points_.size() - 2);
    const double alpha = scaled_t - static_cast<double>(i);

    // Linear interpolation
    const auto& p0 = points_[i];
    const auto& p1 = points_[i + 1];

    const auto p0_params = p0.parameter_coordinates();
    const auto p1_params = p1.parameter_coordinates();

    const double u = p0_params.u() + (p1_params.u() - p0_params.u()) * alpha;
    const double v = p0_params.v() + (p1_params.v() - p0_params.v()) * alpha;

    const auto param = ParameterCoordinate(u, v);
    return surface_->evaluate(param);
}

Vector GeodesicCurve::tangent(double t) const {
    validate_parameter(t);

    if (points_.size() < 2) {
        throw std::runtime_error("Geodesic curve has insufficient points for tangent computation");
    }

    // Find segment containing t
    const auto num_segments = static_cast<double>(points_.size() - 1);
    const double scaled_t = t * num_segments;
    const size_t i = std::min(static_cast<size_t>(scaled_t), points_.size() - 2);

    // Use central difference for interior points
    if (i > 0 && i < points_.size() - 2) {
        return (points_[i+1].world_position() - points_[i-1].world_position()).normalize();
    }

    // Use forward/backward difference at endpoints
    return (points_[i+1].world_position() - points_[i].world_position()).normalize();
}

Vector GeodesicCurve::normal(double t) const {
    validate_parameter(t);
    return evaluate(t).world_normal();
}

void PathSegment::add_point(double t, double u, double v) {
    t_values_.push_back(t);
    u_values_.push_back(u);
    v_values_.push_back(v);
}

SurfacePoint PathSegment::evaluate(double t) const {
    validate_parameter(t);

    if (t_values_.empty()) {
        throw std::runtime_error("Path segment has no points");
    }

    // Find segment containing t
    auto it = std::lower_bound(t_values_.begin(), t_values_.end(), t);
    const size_t i = std::min(
        static_cast<size_t>(std::distance(t_values_.begin(), it)),
        t_values_.size() - 2
    );

    // Linear interpolation
    const double dt = t_values_[i+1] - t_values_[i];
    const double alpha = (t - t_values_[i]) / dt;

    const double u = u_values_[i] + (u_values_[i+1] - u_values_[i]) * alpha;
    const double v = v_values_[i] + (v_values_[i+1] - v_values_[i]) * alpha;

    const auto param = ParameterCoordinate(u, v);
    auto point = surface_->evaluate(param);

    // Diagnostic: Log evaluation details
    const auto start_param = ParameterCoordinate(u_values_[0], v_values_[0]);
    const auto start_point = surface_->evaluate(start_param);
    const auto dist = (point.world_position() - start_point.world_position()).length();

    std::cout << "\nPath Evaluation Diagnostics:\n"
              << "t = " << t << "\n"
              << "Parameters: u=" << u << " v=" << v << "\n"
              << "Position: " << point.world_position().x << ", "
              << point.world_position().y << ", " << point.world_position().z << "\n"
              << "Distance from start: " << dist << "\n";

    return point;
}

Vector PathSegment::tangent(double t) const {
    validate_parameter(t);

    if (t_values_.size() < 2) {
        throw std::runtime_error("Path segment has insufficient points for tangent computation");
    }

    // Find segment containing t
    auto it = std::lower_bound(t_values_.begin(), t_values_.end(), t);
    const size_t i = std::min(
        static_cast<size_t>(std::distance(t_values_.begin(), it)),
        t_values_.size() - 2
    );

    // Use central difference for interior points
    if (i > 0 && i < t_values_.size() - 2) {
        const auto p1_param = ParameterCoordinate(u_values_[i+1], v_values_[i+1]);
        const auto p0_param = ParameterCoordinate(u_values_[i-1], v_values_[i-1]);
        const auto p1 = surface_->evaluate(p1_param);
        const auto p0 = surface_->evaluate(p0_param);
        return (p1.world_position() - p0.world_position()).normalize();
    }

    // Use forward/backward difference at endpoints
    const auto p1_param = ParameterCoordinate(u_values_[i+1], v_values_[i+1]);
    const auto p0_param = ParameterCoordinate(u_values_[i], v_values_[i]);
    const auto p1 = surface_->evaluate(p1_param);
    const auto p0 = surface_->evaluate(p0_param);
    return (p1.world_position() - p0.world_position()).normalize();
}

Vector PathSegment::normal(double t) const {
    validate_parameter(t);
    return evaluate(t).world_normal();
}

void TransitionPath::add_segment(
    std::shared_ptr<Surface> surface,
    double t_start, double t_end,
    double u_start, double u_end,
    double v_start, double v_end,
    const Vector& /*direction*/  // Used by derived classes
) {
    if (!surface) {
        throw std::invalid_argument("Surface pointer cannot be null");
    }

    auto segment = std::make_unique<PathSegment>(
        std::shared_ptr<Surface>(const_cast<Surface*>(surface.get()), [](Surface*){})
    );

    // Adaptive sampling based on surface curvature
    int num_points = BASE_TRANSITION_POINTS;

    // Get surface properties at start
    const auto start_param = ParameterCoordinate(u_start, v_start);
    const auto props = surface->compute_properties(start_param);
    if (props.gaussian_curvature) {
        const double curvature = std::abs(*props.gaussian_curvature);
        num_points += static_cast<int>(5.0 * std::sqrt(curvature));
    }

    // Pre-compute parameter deltas
    const double dt = t_end - t_start;
    const double du = u_end - u_start;
    const double dv = v_end - v_start;

    // Linear interpolation for transition paths
    for (int i = 0; i < num_points; ++i) {
        const double alpha = static_cast<double>(i) / (num_points - 1);
        segment->add_point(
            t_start + dt * alpha,
            u_start + du * alpha,
            v_start + dv * alpha
        );
    }

    segments_.push_back(std::move(segment));
}

SurfacePoint TransitionPath::evaluate(double t) const {
    validate_parameter(t);

    if (segments_.empty()) {
        throw std::runtime_error("Transition path has no segments");
    }

    // Find segment containing t
    for (const auto& segment : segments_) {
        if (t <= segment->t_values().back()) {
            return segment->evaluate(t);
        }
    }

    // If t is beyond last segment, evaluate at end of last segment
    return segments_.back()->evaluate(segments_.back()->t_values().back());
}

Vector TransitionPath::tangent(double t) const {
    validate_parameter(t);

    if (segments_.empty()) {
        throw std::runtime_error("Transition path has no segments");
    }

    // Find segment containing t
    for (const auto& segment : segments_) {
        if (t <= segment->t_values().back()) {
            return segment->tangent(t);
        }
    }

    // If t is beyond last segment, use tangent at end of last segment
    return segments_.back()->tangent(segments_.back()->t_values().back());
}

Vector TransitionPath::normal(double t) const {
    validate_parameter(t);

    if (segments_.empty()) {
        throw std::runtime_error("Transition path has no segments");
    }

    // Find segment containing t
    for (const auto& segment : segments_) {
        if (t <= segment->t_values().back()) {
            return segment->normal(t);
        }
    }

    // If t is beyond last segment, use normal at end of last segment
    return segments_.back()->normal(segments_.back()->t_values().back());
}

} // namespace shap


