SurfacePoint Surface::evaluate(const ParameterCoordinate& param) const {
    const GeometricProperties props = compute_properties(param);
    return SurfacePoint(
        const_cast<Surface*>(this),
        param,
        props.world_position,
        props.world_normal,
        props.world_du,
        props.world_dv
    );
}

ParameterVelocity Surface::world_to_parameter_velocity(
    const Vector& world_direction,
    const Point& world_du,
    const Point& world_dv
) const noexcept {
    // Compute metric tensor components
    const double du_du = dot(world_du, world_du);
    const double du_dv = dot(world_du, world_dv);
    const double dv_dv = dot(world_dv, world_dv);
    const double det = du_du * dv_dv - du_dv * du_dv;

    // Handle near-singular cases
    if (std::abs(det) < EPSILON) {
        // Project onto dominant direction
        if (du_du > dv_dv) {
            return ParameterVelocity(
                dot(world_direction, world_du) / du_du,
                0.0
            );
        } else {
            return ParameterVelocity(
                0.0,
                dot(world_direction, world_dv) / dv_dv
            );
        }
    }

    const double inv_det = 1.0 / det;

    // Project direction onto surface basis
    const double dir_du = dot(world_direction, world_du);
    const double dir_dv = dot(world_direction, world_dv);

    // Apply inverse metric tensor to get parameter velocities
    const double du_param = (dir_du * dv_dv - dir_dv * du_dv) * inv_det;
    const double dv_param = (dir_dv * du_du - dir_du * du_dv) * inv_det;

    return ParameterVelocity(du_param, dv_param);
}

