#include <shap/surface.hpp>
#include <shap/surfaces/flat_patch.hpp>
#include <shap/surface_point.hpp>
#include <shap/path.hpp>
#include "test_utils.hpp"
#include <cassert>
#include <iostream>

namespace shap::test {

// Tests that are currently passing
void test_path_length_invariants_passing() {
    std::cout << "Running passing path length invariant tests...\n\n";

    // Create a flat patch with known scale factors
    auto face = surfaces::create_flat_patch(
        Point(-1, 1, -1),    // origin at top-left
        Vector(2, 0, 0),     // du: scale factor of 2 in x
        Vector(0, 0, 2)      // dv: scale factor of 2 in z
    );

    std::cout << "Test 1: Parameter Space Distance\n";
    {
        // Create a path in u direction
        const Point start(-0.5, 1, 0);  // u = 0.25, v = 0.5
        const Vector dir(1, 0, 0);      // Unit vector in x
        const double length = 1.0;       // One unit in world space

        // Expected parameter space distance:
        // Δu = L/(2|du|) = 1.0/(2*2) = 0.25
        const double expected_param_delta = 0.25;

        // Create path
        const auto params = face->world_to_parameter_space(start);
        const auto start_point = face->evaluate(params);
        auto path = face->create_path(start_point, dir, length);

        // Check parameter space distance
        const auto end_pt = path->evaluate(1.0);
        const double actual_param_delta = end_pt.parameter_coordinates().u() - start_point.parameter_coordinates().u();

        std::cout << "Start parameters: u=" << start_point.parameter_coordinates().u() 
                 << " v=" << start_point.parameter_coordinates().v() << "\n"
                 << "End parameters: u=" << end_pt.parameter_coordinates().u() 
                 << " v=" << end_pt.parameter_coordinates().v() << "\n"
                 << "Expected parameter delta: " << expected_param_delta << "\n"
                 << "Actual parameter delta: " << actual_param_delta << "\n";

        assert(approx_equal(actual_param_delta, expected_param_delta));
    }

    std::cout << "\nAll passing path length invariant tests completed!\n";
}

// Tests that are currently failing and under investigation
void test_path_length_invariants_failing() {
    std::cout << "\nRunning known failing path length invariant tests...\n";
    std::cout << "Note: These tests are expected to fail and are under investigation\n\n";

    // Create a flat patch with known scale factors
    auto face = surfaces::create_flat_patch(
        Point(-1, 1, -1),    // origin at top-left
        Vector(2, 0, 0),     // du: scale factor of 2 in x
        Vector(0, 0, 2)      // dv: scale factor of 2 in z
    );

    std::cout << "Test 2: World Space Distance (Known Issue)\n";
    {
        // Test points along path
        const Point start(-0.5, 1, 0);
        const Vector dir(1, 0, 0);
        const double length = 1.0;

        const auto params = face->world_to_parameter_space(start);
        const auto start_point = face->evaluate(params);
        auto path = face->create_path(start_point, dir, length);

        // Check distances at regular intervals
        for (double t = 0.0; t <= 1.0; t += 0.2) {
            const auto pt = path->evaluate(t);
            const auto pos = pt.world_position();
            const double actual_dist = (pos - start).length();
            const double expected_dist = t * length;

            std::cout << "t = " << t << ":\n";
            print_point("  Position", pos);
            std::cout << "  Expected distance: " << expected_dist << "\n"
                     << "  Actual distance: " << actual_dist << "\n"
                     << "  Error: " << std::abs(actual_dist - expected_dist) << "\n";

            assert(approx_equal(actual_dist, expected_dist));
        }
    }

    std::cout << "\nTest 3: Constant Speed (Known Issue)\n";
    {
        // Test velocity at different points
        const Point start(-0.5, 1, 0);
        const Vector dir(1, 0, 0);
        const double length = 1.0;

        const auto params = face->world_to_parameter_space(start);
        const auto start_point = face->evaluate(params);
        auto path = face->create_path(start_point, dir, length);

        // Check velocity using finite differences
        const double dt = 0.1;
        for (double t = 0.0; t <= 1.0 - dt; t += 0.2) {
            const auto pt1 = path->evaluate(t);
            const auto pt2 = path->evaluate(t + dt);
            const double velocity = (pt2.world_position() - pt1.world_position()).length() / dt;

            std::cout << "t = " << t << ":\n"
                     << "  Velocity: " << velocity << "\n"
                     << "  Expected: " << length << "\n"
                     << "  Error: " << std::abs(velocity - length) << "\n";

            assert(approx_equal(velocity, length));
        }
    }
}

} // namespace shap::test

int main() {
    try {
        // Run passing tests first
        shap::test::test_path_length_invariants_passing();

        // Run failing tests separately
        std::cout << "\n----------------------------------------\n";
        std::cout << "Running tests with known failures:\n";
        std::cout << "----------------------------------------\n";
        shap::test::test_path_length_invariants_failing();
        return 0;
    }
    catch (const std::exception& e) {
        std::cerr << "Test failed: " << e.what() << "\n";
        return 1;
    }
}


