#pragma once
#include <cstdint>
#include <utility>
#include <stdexcept>

namespace shap {

// Strong type for parameter space coordinates
class ParameterCoordinate {
public:
    explicit ParameterCoordinate(double u, double v) : u_(u), v_(v) {
        validate();
    }

    [[nodiscard]] double u() const noexcept { return u_; }
    [[nodiscard]] double v() const noexcept { return v_; }

    // Arithmetic operations preserve parameter space bounds
    ParameterCoordinate operator+(const ParameterCoordinate& other) const {
        return ParameterCoordinate(u_ + other.u_, v_ + other.v_);
    }

    ParameterCoordinate operator*(double scale) const {
        return ParameterCoordinate(u_ * scale, v_ * scale);
    }

private:
    void validate() const {
        if (u_ < 0.0 || u_ > 1.0 || v_ < 0.0 || v_ > 1.0) {
            throw std::invalid_argument("Parameter coordinates must be in [0,1]");
        }
    }

    double u_, v_;
};

// Strong type for parameter space velocities/derivatives
class ParameterVelocity {
public:
    explicit ParameterVelocity(double du, double dv) : du_(du), dv_(dv) {}

    [[nodiscard]] double du() const noexcept { return du_; }
    [[nodiscard]] double dv() const noexcept { return dv_; }

    ParameterVelocity operator*(double scale) const {
        return ParameterVelocity(du_ * scale, dv_ * scale);
    }

private:
    double du_, dv_;
};


// Parameter space indices
enum class ParamIndex : uint8_t {
    U = 0,
    V = 1
};

// Parameter space bounds
enum class ParamBound : uint8_t {
    Lower = 0,
    Upper = 1
};

// Surface type classification
enum class SurfaceType : uint8_t {
    Generic,    // Base type for surfaces
    Smooth,     // No singularities or edges
    Developable, // Zero Gaussian curvature
    Singular    // Contains singularities or edges
};

// Edge descriptor for surface points
struct EdgeDescriptor {
    ParamIndex param;    // Which parameter (u or v) is on boundary
    ParamBound bound;    // Which boundary (lower=0 or upper=1)
    double edge_param;   // Position along the edge [0,1]

    // Equality operator
    bool operator==(const EdgeDescriptor& other) const noexcept {
        return param == other.param &&
               bound == other.bound &&
               edge_param == other.edge_param;
    }

    // Get u,v parameters for a point on this edge
    [[nodiscard]] std::pair<double, double> get_params(double t) const noexcept {
        double u = param == ParamIndex::U ? (bound == ParamBound::Lower ? 0.0 : 1.0) : t;
        double v = param == ParamIndex::V ? (bound == ParamBound::Lower ? 0.0 : 1.0) : t;
        return {u, v};
    }

    // Get the parameter that varies along the edge
    [[nodiscard]] ParamIndex free_param() const noexcept {
        return param == ParamIndex::U ? ParamIndex::V : ParamIndex::U;
    }
};

// Connection between surface edges
struct EdgeConnection {
    EdgeDescriptor edge1;  // Edge descriptor for first surface
    EdgeDescriptor edge2;  // Edge descriptor for second surface
    int orientation;       // +1 if parameters flow in same direction, -1 if opposite

    // Map parameter from edge1 to edge2 based on orientation
    [[nodiscard]] double map_parameter(double param) const noexcept {
        return orientation > 0 ? param : 1.0 - param;
    }
};

// Arithmetic operators for parameter indices
inline int operator-(ParamIndex a, ParamIndex b) {
    return static_cast<int>(a) - static_cast<int>(b);
}

inline ParamIndex operator+(ParamIndex a, int b) {
    return static_cast<ParamIndex>(static_cast<int>(a) + b);
}

inline ParamIndex operator-(ParamIndex a, int b) {
    return static_cast<ParamIndex>(static_cast<int>(a) - b);
}

// Arithmetic operators for parameter bounds
inline int operator-(ParamBound a, ParamBound b) {
    return static_cast<int>(a) - static_cast<int>(b);
}

inline ParamBound operator+(ParamBound a, int b) {
    return static_cast<ParamBound>(static_cast<int>(a) + b);
}

inline ParamBound operator-(ParamBound a, int b) {
    return static_cast<ParamBound>(static_cast<int>(a) - b);
}

// Helper function to subtract parameter pairs
inline std::pair<double, double> operator-(
    const std::pair<double, double>& a,
    const std::pair<double, double>& b
) {
    return {a.first - b.first, a.second - b.second};
}

} // namespace shap


