#pragma once
#include <cmath>
#include <compare>

namespace shap {

class Point {
public:
    double x, y, z;

    constexpr Point(double x_ = 0, double y_ = 0, double z_ = 0) noexcept
        : x(x_), y(y_), z(z_) {}

    constexpr Point operator+(const Point& other) const noexcept {
        return Point(x + other.x, y + other.y, z + other.z);
    }

    constexpr Point operator-(const Point& other) const noexcept {
        return Point(x - other.x, y - other.y, z - other.z);
    }

    constexpr Point operator-() const noexcept {
        return Point(-x, -y, -z);
    }

    constexpr Point operator*(double s) const noexcept {
        return Point(x * s, y * s, z * s);
    }

    constexpr Point operator/(double s) const noexcept {
        return Point(x / s, y / s, z / s);
    }

    [[nodiscard]] double length() const noexcept {
        return std::sqrt(x*x + y*y + z*z);
    }

    [[nodiscard]] double length_squared() const noexcept {
        return x*x + y*y + z*z;
    }

    [[nodiscard]] Point normalize() const noexcept {
        double len = length();
        if (len < 1e-10) return Point();
        return *this / len;
    }

    // Three-way comparison operator (enables all comparison operators)
    auto operator<=>(const Point&) const = default;

    // Explicit equality operator for better performance
    bool operator==(const Point& other) const noexcept {
        return x == other.x && y == other.y && z == other.z;
    }
};

using Vector = Point;

constexpr Point operator*(double s, const Point& p) noexcept {
    return p * s;
}

[[nodiscard]] constexpr double dot(const Point& a, const Point& b) noexcept {
    return a.x * b.x + a.y * b.y + a.z * b.z;
}

[[nodiscard]] constexpr Point cross(const Point& a, const Point& b) noexcept {
    return Point(
        a.y * b.z - a.z * b.y,
        a.z * b.x - a.x * b.z,
        a.x * b.y - a.y * b.x
    );
}

} // namespace shap


