#pragma once
#include "types.hpp"
#include "point.hpp"
#include <memory>
#include <optional>

namespace shap {

class Surface;

/**
 * Represents a point on a surface with associated geometric properties.
 * Provides clear separation between parameter space coordinates and world space properties.
 */
class SurfacePoint {
public:
    SurfacePoint(
        Surface* surface,
        const ParameterCoordinate& param,
        Point world_pos,
        Vector world_norm,
        Vector world_du,
        Vector world_dv
    ) : surface_(surface)
      , param_(param)
      , world_pos_(std::move(world_pos))
      , world_normal_(std::move(world_norm))
      , world_du_(std::move(world_du))
      , world_dv_(std::move(world_dv)) {}

    // Parameter space access
    [[nodiscard]] const ParameterCoordinate& parameter_coordinates() const noexcept { 
        return param_; 
    }

    // World space access
    [[nodiscard]] const Point& world_pos() const noexcept { return world_pos_; }
    [[nodiscard]] const Vector& world_normal() const noexcept { return world_normal_; }

    // Surface derivatives (world space)
    [[nodiscard]] const Vector& world_du() const noexcept { return world_du_; }
    [[nodiscard]] const Vector& world_dv() const noexcept { return world_dv_; }

    // Surface access
    [[nodiscard]] Surface* surface() const noexcept { return surface_; }

    // Scale factors for space conversion
    [[nodiscard]] std::pair<double, double> get_scale_factors() const {
        return {world_du_.length(), world_dv_.length()};
    }

    // Edge information
    [[nodiscard]] bool is_on_edge() const noexcept {
        const auto coords = parameter_coordinates();
        return coords.u() == 0.0 || coords.u() == 1.0 ||
               coords.v() == 0.0 || coords.v() == 1.0;
    }

    [[nodiscard]] std::optional<EdgeDescriptor> get_edge_descriptor() const noexcept {
        if (!is_on_edge()) return std::nullopt;

        const auto coords = parameter_coordinates();
        if (coords.u() == 0.0) return EdgeDescriptor{ParamIndex::U, ParamBound::Lower, coords.v()};
        if (coords.u() == 1.0) return EdgeDescriptor{ParamIndex::U, ParamBound::Upper, coords.v()};
        if (coords.v() == 0.0) return EdgeDescriptor{ParamIndex::V, ParamBound::Lower, coords.u()};
        if (coords.v() == 1.0) return EdgeDescriptor{ParamIndex::V, ParamBound::Upper, coords.u()};
        return std::nullopt;
    }

private:
    Surface* surface_;              // Non-owning pointer to surface
    ParameterCoordinate param_;     // Parameter space coordinates
    Point world_pos_;              // Position in world space
    Vector world_normal_;           // Surface normal in world space
    Vector world_du_;              // First derivative in u direction (world space)
    Vector world_dv_;              // First derivative in v direction (world space)
};

} // namespace shap


