namespace shap {

/**
 * Represents a point on a surface with associated geometric properties.
 * Combines local parameter space coordinates with world space geometric information.
 */
class GeometryR2Point {
public:
    GeometryR2Point(
        LocalR2Point local,
        Point world_pos,
        Vector world_normal,
        Vector world_du,
        Vector world_dv
    ) : local_pos_(std::move(local))
      , world_pos_(std::move(world_pos))
      , world_normal_(std::move(world_normal).normalize())
      , world_du_(std::move(world_du))
      , world_dv_(std::move(world_dv)) {}

    // Local parameter space access
    [[nodiscard]] const LocalR2Point& local_pos() const noexcept { 
        return local_pos_; 
    }

    [[nodiscard]] double u() const noexcept { return local_pos_.u(); }
    [[nodiscard]] double v() const noexcept { return local_pos_.v(); }

    // World space access
    [[nodiscard]] const Point& world_pos() const noexcept { return world_pos_; }
    [[nodiscard]] const Vector& world_normal() const noexcept { return world_normal_; }
    [[nodiscard]] const Vector& world_du() const noexcept { return world_du_; }
    [[nodiscard]] const Vector& world_dv() const noexcept { return world_dv_; }

    // Scale factors for space conversion
    [[nodiscard]] std::pair<double, double> get_scale_factors() const {
        return {world_du_.length(), world_dv_.length()};
    }

    // Edge information
    [[nodiscard]] bool is_on_edge() const noexcept {
        return u() == 0.0 || u() == 1.0 || v() == 0.0 || v() == 1.0;
    }

    [[nodiscard]] std::optional<EdgeDescriptor> get_edge_descriptor() const noexcept {
        if (!is_on_edge()) return std::nullopt;

        if (u() == 0.0) return EdgeDescriptor{ParamIndex::U, ParamBound::Lower, v()};
        if (u() == 1.0) return EdgeDescriptor{ParamIndex::U, ParamBound::Upper, v()};
        if (v() == 0.0) return EdgeDescriptor{ParamIndex::V, ParamBound::Lower, u()};
        if (v() == 1.0) return EdgeDescriptor{ParamIndex::V, ParamBound::Upper, u()};
        return std::nullopt;
    }

