{"tool":"newFileCreated","path":"include/shap/local_point2.hpp","content":"#pragma once
#include "validation_config.hpp"
#include <stdexcept>

namespace shap {

/**
 * Point in 2D local parameter space [0,1]×[0,1].
 * Represents normalized coordinates in a surface's parameter space.
 */
class LocalPoint2 {
public:
    /**
     * Construct a point in local parameter space.
     * @param u First parameter coordinate
     * @param v Second parameter coordinate
     * @throws std::invalid_argument if coordinates are outside [0,1]
     */
    explicit LocalPoint2(double u, double v) : u_(u), v_(v) {
        validate();
    }

    // Parameter access
    [[nodiscard]] double u() const noexcept { return u_; }
    [[nodiscard]] double v() const noexcept { return v_; }

    // Arithmetic operations preserve parameter space bounds
    LocalPoint2 operator+(const LocalPoint2& other) const {
        return LocalPoint2(u_ + other.u_, v_ + other.v_);
    }

    LocalPoint2 operator*(double scale) const {
        return LocalPoint2(u_ * scale, v_ * scale);
    }

private:
    void validate() const {
        if (u_ < 0.0 || u_ > 1.0 || v_ < 0.0 || v_ > 1.0) {
            throw std::invalid_argument("Parameter coordinates must be in [0,1]");
        }
    }

    double u_, v_;
};

} // namespace shap"}
