            }

            if (dvv_func_) {
                dvv = (*dvv_func_)(local);
            } else {
                const double hv = compute_step_size(local.v());
                const auto v_plus = LocalPoint2(local.u(), local.v() + hv);
                const auto v_minus = LocalPoint2(local.u(), local.v() - hv);
                dvv = (position_func_(v_plus) - position * 2.0 +
