        return ThisType(
            coords_[1] * other.coords_[2] - coords_[2] * other.coords_[1],
            coords_[2] * other.coords_[0] - coords_[0] * other.coords_[2],
            coords_[0] * other.coords_[1] - coords_[1] * other.coords_[0]
        );
    }
};

