    // Construct temporary metric tensor from derivatives
    Surface2DMetricTensor metric(
        world_du.dot(world_du),  // g11
        world_du.dot(world_dv),  // g12
        world_du.dot(world_dv),  // g21 (symmetric)
        world_dv.dot(world_dv)   // g22
    );

    // Use metric tensor machinery for the conversion
    ...
}

