class FunctionSurface final : public Surface {
public:
    FunctionSurface(
        PositionFunction pos,
        DerivativeFunction du,
        DerivativeFunction dv,
        DerivativeFunction duu,
        DerivativeFunction duv,
        DerivativeFunction dvv,
        CurvatureFunction gaussian,
