    std::cout << "Parameter velocity (includes world length):\n"
              << "  du/dt = " << param_vel.x() << "\n"
              << "  dv/dt = " << param_vel.y() << "\n";

    // Compute end parameters using scaled velocity
    const auto& start_local = start.local_pos();
