namespace shap {

Surface3D::Surface3D(
    PositionFunction position_func,
    DerivativeFunction du_func,
    DerivativeFunction dv_func,
    DerivativeFunction duu_func,
    DerivativeFunction duv_func,
    DerivativeFunction dvv_func,
    CurvatureFunction gaussian_func,
    CurvatureFunction mean_func,
    std::optional<PathSolver> path_solver,
    SurfaceType type,
    ParameterSpaceDerivative du2_du,
    ParameterSpaceDerivative du2_dv,
    ParameterSpaceDerivative duv_du,
    ParameterSpaceDerivative duv_dv,
    ParameterSpaceDerivative dv2_du,
    ParameterSpaceDerivative dv2_dv
) : position_func_(std::move(position_func))
  , du_func_(std::move(du_func))
  , dv_func_(std::move(dv_func))
  , duu_func_(std::move(duu_func))
  , duv_func_(std::move(duv_func))
  , dvv_func_(std::move(dvv_func))
  , gaussian_curv_func_(std::move(gaussian_func))
  , mean_curv_func_(std::move(mean_func))
  , path_solver_(std::move(path_solver))
  , type_(type)
  , du2_du_fn_(std::move(du2_du))
  , du2_dv_fn_(std::move(du2_dv))
  , duv_du_fn_(std::move(duv_du))
  , duv_dv_fn_(std::move(duv_dv))
  , dv2_du_fn_(std::move(dv2_du))
  , dv2_dv_fn_(std::move(dv2_dv)) {

    // Validate required functions are provided
    if (!position_func_ || !du_func_ || !dv_func_) {
        throw std::invalid_argument(
            "Position and first derivative functions are required"
        );
    }

    // Higher derivatives are required for non-flat surfaces
    if (type != SurfaceType::Flat && 
        (!duu_func_ || !duv_func_ || !dvv_func_ || 
         !gaussian_curv_func_ || !mean_curv_func_)) {
        throw std::invalid_argument(
            "Second derivatives and curvature functions required for non-flat surfaces"
        );
    }
}

GeometricPoint<2, 3, WorldSpaceTag>
Surface3D::evaluate(const ParameterPoint& param) const {
    validate_parameters(param);

    // Get position and derivatives
    WorldPoint3 world_pos = position_func_(param);
    WorldVector3 du = du_func_(param);
    WorldVector3 dv = dv_func_(param);

    // Bundle into GeometricPoint
    std::vector<TargetVector> derivs{du, dv};
    return GeometricPoint<2, 3, WorldSpaceTag>(
        this, param, world_pos, std::move(derivs)
    );
}

std::vector<Surface3D::TargetVector>
Surface3D::derivatives(const ParameterPoint& param) const {
    validate_parameters(param);

    // Return first derivatives in u and v directions
    return {
        du_func_(param),
        dv_func_(param)
    };
}

std::pair<double, double>
Surface3D::get_scale_factors(const ParameterPoint& local) const {
    validate_parameters(local);

    // Scale factors are lengths of first derivatives
    WorldVector3 du = du_func_(local);
    WorldVector3 dv = dv_func_(local);

    return {du.length(), dv.length()};
}

} // namespace shap

