std::vector<Surface3DImpl::TargetVector>
Surface3DImpl::derivatives(const ParameterPoint& param) const {
    validate_parameters(param);

    // Return first derivatives in u and v directions
    return {
        du_func_(param),
        dv_func_(param)
    };
}

std::pair<double, double>
Surface3DImpl::get_scale_factors(const ParameterPoint& local) const {
    validate_parameters(local);

    // Scale factors are lengths of first derivatives
    WorldVector3 du = du_func_(local);
    WorldVector3 dv = dv_func_(local);

    return {du.length(), dv.length()};
}

ParamPoint3 Surface3DImpl::world_to_param(const WorldPoint3& pos) const {
    // Project point onto surface using path solver
    if (!path_solver_) {
        throw std::runtime_error("No path solver available for world_to_param");
    }

    // Get surface normal at origin as initial direction
    const auto origin_point = evaluate(ParamPoint2(0.5, 0.5));
    const auto normal = origin_point.derivatives()[0].crossed(origin_point.derivatives()[1]).normalized();

    // Try to find intersection with surface
    const auto intersection = path_solver_(pos, normal, 1000.0);
    if (!intersection) {
        throw std::invalid_argument("Could not project point onto surface");
    }

    // Return parameter coordinates with signed distance
    return ParamPoint3(intersection->position, intersection->edge_parameter, 0.0);
}

