#pragma once
#include "coord.hpp"
#include <vector>
#include <stdexcept>

namespace shap {

// Forward declarations
template<int ParamDim, int TargetDim, typename SpaceTag>
class GeometricPoint;

class Path3D;
class SurfacePath;

/**
 * Base class for manifolds that map from parameter space to target space.
 * 
 * @tparam ParamDim Dimension of parameter space (1 for paths, 2/3 for surfaces)
 * @tparam TargetDim Dimension of target space (2 or 3 for world space)
 * @tparam SpaceTag WorldSpaceTag or ParamSpaceTag for target space
 */
template<int ParamDim, int TargetDim, typename SpaceTag>
class Manifold {
public:
    static_assert(ParamDim >= 1 && ParamDim <= 3, "Parameter space must be 1D, 2D, or 3D");
    static_assert(TargetDim >= 2 && TargetDim <= 3, "Target space must be 2D or 3D");
    static_assert(ParamDim <= TargetDim || std::is_same_v<SpaceTag, ParamSpaceTag>,
                 "Parameter dimension cannot exceed target dimension in world space");

    using ParameterPoint = Coord<ParamDim, PointTag, ParamSpaceTag>;
    using TargetPoint = Coord<TargetDim, PointTag, SpaceTag>;
    using TargetVector = Coord<TargetDim, VectorTag, SpaceTag>;

    virtual ~Manifold() = default;

    /**
     * Evaluate the manifold at a parameter point.
     * 
     * @param param Parameter space coordinates
     * @return GeometricPoint containing full geometric information
     * @throws std::invalid_argument if coordinates are invalid
     */
    [[nodiscard]] virtual GeometricPoint<ParamDim, TargetDim, SpaceTag> 
    evaluate(const ParameterPoint& param) const = 0;

    /**
     * Get derivatives at parameter point.
     * Returns vectors representing how the manifold changes in each parameter direction.
     * 
     * @param param Parameter space coordinates
     * @return Vector of derivative vectors, size matches ParamDim
     * @throws std::invalid_argument if coordinates are invalid
     */
    [[nodiscard]] virtual std::vector<TargetVector>
    derivatives(const ParameterPoint& param) const = 0;

protected:
    // Validate parameter values are in [0,1]
    static void validate_parameters(const ParameterPoint& param) {
        for (int i = 0; i < ParamDim; ++i) {
            if (param[i] < 0.0 || param[i] > 1.0) {
                throw std::invalid_argument("Parameter values must be in [0,1]");
            }
        }
    }
};

// Common manifold type aliases
using WorldPath3D = Manifold<1, 3, WorldSpaceTag>;    // 1D -> 3D world
using WorldPath2D = Manifold<1, 2, WorldSpaceTag>;    // 1D -> 2D world
using Surface2D = Manifold<2, 2, WorldSpaceTag>;      // 2D -> 2D world
using ParamPath2D = Manifold<1, 2, ParamSpaceTag>;    // 1D -> 2D param
using ParamPath3D = Manifold<1, 3, ParamSpaceTag>;    // 1D -> 3D param
using ParamSurface2D = Manifold<2, 2, ParamSpaceTag>; // 2D -> 2D param
using ParamSurface3D = Manifold<3, 3, ParamSpaceTag>; // 3D -> 3D param

} // namespace shap

#pragma once
#include "manifold.hpp"
#include <memory>
#include <vector>

namespace shap {

/**
 * Bundles geometric information about a point on a manifold.
 * Replaces GeometryPoint2 with a more general template that works for both paths and surfaces.
 * 
 * @tparam ParamDim Dimension of parameter space (1 for paths, 2/3 for surfaces)
 * @tparam TargetDim Dimension of target space (2 or 3 for world space)
 * @tparam SpaceTag WorldSpaceTag or ParamSpaceTag for target space
 */
template<int ParamDim, int TargetDim, typename SpaceTag>
class GeometricPoint {
public:
    using ManifoldType = Manifold<ParamDim, TargetDim, SpaceTag>;
    using ParameterPoint = typename ManifoldType::ParameterPoint;
    using TargetPoint = typename ManifoldType::TargetPoint;
    using TargetVector = typename ManifoldType::TargetVector;

    GeometricPoint(
        const ManifoldType* manifold,
        ParameterPoint local_pos,
        TargetPoint world_pos,
        std::vector<TargetVector> derivatives
    ) : manifold_(manifold)
      , local_pos_(std::move(local_pos))
      , world_pos_(std::move(world_pos))
      , derivatives_(std::move(derivatives)) {
        if (!manifold) {
            throw std::invalid_argument("Manifold pointer cannot be null");
        }
        if (derivatives_.size() != ParamDim) {
            throw std::invalid_argument("Number of derivatives must match parameter dimension");
        }
    }

    // Accessors
    [[nodiscard]] const ManifoldType* manifold() const noexcept { return manifold_; }
    [[nodiscard]] const ParameterPoint& local_pos() const noexcept { return local_pos_; }
    [[nodiscard]] const TargetPoint& world_pos() const noexcept { return world_pos_; }

    // Get first derivatives
    [[nodiscard]] const std::vector<TargetVector>& derivatives() const noexcept {
        return derivatives_;
    }

    // Convenience accessors for specific derivatives
    template<int Dim = ParamDim>
    [[nodiscard]] std::enable_if_t<(Dim >= 1), const TargetVector&> 
    d_u() const noexcept {
        return derivatives_[0];
    }

    template<int Dim = ParamDim>
    [[nodiscard]] std::enable_if_t<(Dim >= 2), const TargetVector&>
    d_v() const noexcept {
        return derivatives_[1];
    }

    template<int Dim = ParamDim>
    [[nodiscard]] std::enable_if_t<(Dim >= 3), const TargetVector&>
    d_w() const noexcept {
        return derivatives_[2];
    }

private:
    const ManifoldType* manifold_;
    ParameterPoint local_pos_;
    TargetPoint world_pos_;
    std::vector<TargetVector> derivatives_;
};

} // namespace shap

