    // Constants for numerical integration
    constexpr int GEODESIC_STEPS = 50;
    constexpr double GEODESIC_DT = 1.0 / GEODESIC_STEPS;
    constexpr double CURVATURE_EPSILON = 1e-10;
    constexpr int BASE_TRANSITION_POINTS = 10;

    // Helper for RK4 integration
    struct RK4State {
        double u v;      // Position
        double up vp;    // Velocity
    };

}

GeodesicCurve::GeodesicCurve(
    std::shared_ptr<Surface3D> surface
    const GeometricPoint<2 3 WorldSpaceTag>& start
    const GeometricPoint<2 3 WorldSpaceTag>& end
) : Path3D(
        std::bind(&GeodesicCurve::evaluate_position this std::placeholders::_1)
        std::bind(&GeodesicCurve::evaluate_tangent this std::placeholders::_1)
        std::bind(&GeodesicCurve::evaluate_normal this std::placeholders::_1)
    )
     surface_(std::move(surface))
{
    compute_developable_geodesic(start end);
}

WorldPoint3 GeodesicCurve::evaluate_position(double t) const {
    const auto param = ParamPoint1(t);
    if (points_.empty()) {
        throw std::runtime_error("Geodesic curve has no points");
    }
    const auto num_segments = static_cast<double>(points_.size() - 1);
    const double scaled_t = static_cast<double>(param) * num_segments;
    const size_t idx = std::min(static_cast<size_t>(scaled_t) points_.size() - 2);
    const double alpha = scaled_t - static_cast<double>(idx);
    const auto& p0 = points_[idx];
    const auto& p1 = points_[idx + 1];
    const auto& p0_local = p0.local_pos();
    const auto& p1_local = p1.local_pos();
    const double u = p0_local[0] + (p1_local[0] - p0_local[0]) * alpha;
    const double v = p0_local[1] + (p1_local[1] - p0_local[1]) * alpha;
    const auto local = ParamPoint2(u v);
    return surface_->evaluate(local).world_pos();
}

WorldVector3 GeodesicCurve::evaluate_tangent(double t) const {
    const auto param = ParamPoint1(t);
    if (points_.size() < 2) {
        throw std::runtime_error("Geodesic curve has insufficient points for tangent computation");
    }
    const auto num_segments = static_cast<double>(points_.size() - 1);
    const double scaled_t = static_cast<double>(param) * num_segments;
    const size_t idx = std::min(static_cast<size_t>(scaled_t) points_.size() - 2);
    if (idx > 0 && idx < points_.size() - 2) {
        const WorldVector3 diff = points_[idx+1].world_pos() - points_[idx-1].world_pos();
        return diff.normalized();
    }
    const WorldVector3 diff = points_[idx+1].world_pos() - points_[idx].world_pos();
    return diff.normalized();
}

WorldVector3 GeodesicCurve::evaluate_normal(double t) const {
    ParamPoint1(t); // Validate parameter
    const auto local = ParamPoint2(points_[0].local_pos()[0] points_[0].local_pos()[1]);
    const auto geom = surface_->evaluate(local);
    const auto& derivs = geom.derivatives();
    return derivs[0].crossed(derivs[1]).normalized();
}

void GeodesicCurve::compute_developable_geodesic(
    const GeometricPoint<2 3 WorldSpaceTag>& start
    const GeometricPoint<2 3 WorldSpaceTag>& end
) {
    constexpr int steps = 20;
    points_.clear();
    points_.reserve(steps + 1);

    const auto& start_local = start.local_pos();
    const auto& end_local = end.local_pos();
    const double du = end_local[0] - start_local[0];
    const double dv = end_local[1] - start_local[1];

    for (int i = 0; i <= steps; ++i) {
        const double t = static_cast<double>(i) / steps;
        const double u = start_local[0] + t * du;
        const double v = start_local[1] + t * dv;
        const auto local = ParamPoint2(u v);
        points_.push_back(surface_->evaluate(local));
    }
}

void PathSegment::add_point(double t double u double v) {
    t_values_.push_back(t);
    u_values_.push_back(u);
    v_values_.push_back(v);
}

PathSegment::PathSegment(std::shared_ptr<Surface3D> surface)
    : Path3D(
        std::bind(&PathSegment::evaluate_position this std::placeholders::_1)
        std::bind(&PathSegment::evaluate_tangent this std::placeholders::_1)
        std::bind(&PathSegment::evaluate_normal this std::placeholders::_1)
    )
     surface_(std::move(surface))
{
    // Pre-allocate space for typical path size
    t_values_.reserve(100);
    u_values_.reserve(100);
    v_values_.reserve(100);
}

WorldPoint3 PathSegment::evaluate_position(double t) const {
    const auto param = ParamPoint1(t);
    if (t_values_.empty()) {
        throw std::runtime_error("Path segment has no points");
    }
    if (static_cast<double>(param) <= t_values_.front()) {
        const auto local = ParamPoint2(u_values_.front() v_values_.front());
        return surface_->evaluate(local).world_pos();
    }
    if (static_cast<double>(param) >= t_values_.back()) {
        const auto local = ParamPoint2(u_values_.back() v_values_.back());
        return surface_->evaluate(local).world_pos();
    }
    auto it = std::upper_bound(t_values_.begin() t_values_.end() static_cast<double>(param));
    if (it == t_values_.begin() || it == t_values_.end()) {
        throw std::runtime_error("Path parameter t outside stored range");
    }
    const size_t segment_idx = std::distance(t_values_.begin() it) - 1;
    const double dt = t_values_[segment_idx+1] - t_values_[segment_idx];
    const double alpha = (static_cast<double>(param) - t_values_[segment_idx]) / dt;
    const double u = u_values_[segment_idx] + (u_values_[segment_idx+1] - u_values_[segment_idx]) * alpha;
    const double v = v_values_[segment_idx] + (v_values_[segment_idx+1] - v_values_[segment_idx]) * alpha;
    const auto local = ParamPoint2(u v);
    auto geom = surface_->evaluate(local);
    return geom.world_pos();
}

WorldVector3 PathSegment::evaluate_tangent(double t) const {
    const auto param = ParamPoint1(t);
    if (t_values_.size() < 2) {
        throw std::runtime_error("Path segment has insufficient points for tangent computation");
    }
    auto it = std::lower_bound(t_values_.begin() t_values_.end() static_cast<double>(param));
    const size_t idx = std::min(
        static_cast<size_t>(std::distance(t_values_.begin() it))
        t_values_.size() - 2
    );
    if (idx > 0 && idx < t_values_.size() - 2) {
        const auto p1_local = ParamPoint2(u_values_[idx+1] v_values_[idx+1]);
        const auto p0_local = ParamPoint2(u_values_[idx-1] v_values_[idx-1]);
        const auto p1 = surface_->evaluate(p1_local);
        const auto p0 = surface_->evaluate(p0_local);
        const WorldVector3 diff = p1.world_pos() - p0.world_pos();
        return diff.normalized();
    }
    const auto p1_local = ParamPoint2(u_values_[idx+1] v_values_[idx+1]);
    const auto p0_local = ParamPoint2(u_values_[idx] v_values_[idx]);
    const auto p1 = surface_->evaluate(p1_local);
    const auto p0 = surface_->evaluate(p0_local);
    const WorldVector3 diff = p1.world_pos() - p0.world_pos();
    return diff.normalized();
}

WorldVector3 PathSegment::evaluate_normal(double t) const {
    ParamPoint1(t); // Validate parameter
    if (t_values_.empty()) {
        throw std::runtime_error("Path segment has no points");
    }
    const auto local = ParamPoint2(u_values_[0] v_values_[0]);
    const auto geom = surface_->evaluate(local);
    const auto& derivs = geom.derivatives();
    return derivs[0].crossed(derivs[1]).normalized();
}

void TransitionPath::add_segment(
    std::shared_ptr<Surface3D> surface
    double t_start double t_end
    double u_start double u_end
    double v_start double v_end
    const WorldVector3& /*direction*/  // Used by derived classes
) {
    if (!surface) {
        throw std::invalid_argument("Surface pointer cannot be null");
    }

    auto segment = std::make_unique<PathSegment>(surface);

    // Adaptive sampling based on surface curvature
    int num_points = BASE_TRANSITION_POINTS;

    // Get surface properties at start
    const auto start_local = ParamPoint2(u_start v_start);
    const auto geom = surface->evaluate(start_local);
    // Calculate Gaussian curvature from derivatives
    const auto& derivs = geom.derivatives();
    const auto& du_vec = derivs[0];
    const auto& dv_vec = derivs[1];
    const auto normal = du_vec.crossed(dv_vec).normalized();
    const double curvature = std::abs(normal.dot(du_vec.crossed(dv_vec)));
    if (curvature > CURVATURE_EPSILON) {
        num_points += static_cast<int>(5.0 * std::sqrt(curvature));
    }

    // Pre-compute parameter deltas for interpolation
    const double dt_param = t_end - t_start;
    const double du_param = u_end - u_start;
    const double dv_param = v_end - v_start;

    // Linear interpolation for transition paths
    for (int i = 0; i < num_points; ++i) {
        const double alpha = static_cast<double>(i) / (num_points - 1);
        segment->add_point(
            t_start + dt_param * alpha
            u_start + du_param * alpha
            v_start + dv_param * alpha
        );
    }

    segments_.push_back(std::move(segment));
}

TransitionPath::TransitionPath()
    : Path3D(
        std::bind(&TransitionPath::evaluate_position this std::placeholders::_1)
        std::bind(&TransitionPath::evaluate_tangent this std::placeholders::_1)
