    )
{}

WorldPoint3 TransitionPath::evaluate_position(double t) const {
    const auto param = ParamPoint1(t);
    if (segments_.empty()) {
        throw std::runtime_error("Transition path has no segments");
    }
    for (const auto& segment : segments_) {
        if (static_cast<double>(param) <= segment->t_values().back()) {
            const auto local = ParamPoint2(
                segment->u_values().back()
                segment->v_values().back()
            );
            return segment->surface()->evaluate(local).world_pos();
        }
    }
    const auto& last = segments_.back();
    const auto local = ParamPoint2(
        last->u_values().back()
        last->v_values().back()
    );
    return last->surface()->evaluate(local).world_pos();
}

WorldVector3 TransitionPath::evaluate_tangent(double t) const {
    const auto param = ParamPoint1(t);
    if (segments_.empty()) {
        throw std::runtime_error("Transition path has no segments");
    }
    for (const auto& segment : segments_) {
        if (t <= segment->t_values().back()) {
            const auto& t_vals = segment->t_values();
            const auto& u_vals = segment->u_values();
            const auto& v_vals = segment->v_values();
            auto it = std::lower_bound(t_vals.begin() t_vals.end() t);
            const size_t idx = std::min(
                static_cast<size_t>(std::distance(t_vals.begin() it))
                t_vals.size() - 2
            );
            const auto p1_local = ParamPoint2(u_vals[idx+1] v_vals[idx+1]);
            const auto p0_local = ParamPoint2(u_vals[idx] v_vals[idx]);
            const auto p1 = segment->surface()->evaluate(p1_local);
            const auto p0 = segment->surface()->evaluate(p0_local);
            return (p1.world_pos() - p0.world_pos()).normalized();
        }
    }
    const auto& last = segments_.back();
    const auto& t_vals = last->t_values();
    const auto& u_vals = last->u_values();
    const auto& v_vals = last->v_values();
    const size_t idx = t_vals.size() - 2;
    const auto p1_local = ParamPoint2(u_vals[idx+1] v_vals[idx+1]);
    const auto p0_local = ParamPoint2(u_vals[idx] v_vals[idx]);
