void GeodesicCurve::compute_developable_geodesic(
    const GeometricPoint<2, 3, WorldSpaceTag>& start,
    const GeometricPoint<2, 3, WorldSpaceTag>& end
) {
    // For spheres, use great circle arcs
    if (surface_->surface_type() == SurfaceType::Smooth && 
        std::abs(surface_->gaussian_curv_func_(start.local_pos()) - 
                surface_->gaussian_curv_func_(end.local_pos())) < CURVATURE_EPSILON) {
        compute_smooth_geodesic(start, end);
        return;
    }

    // For developable surfaces, use straight line in parameter space
    if (surface_->surface_type() == SurfaceType::Developable) {
        points_.clear();
        points_.reserve(2);  // Only need start and end for exact line
        points_.push_back(start);
        points_.push_back(end);
        return;
    }

    // Fallback to numerical approximation for other surfaces
    constexpr int steps = 20;
    points_.clear();
    points_.reserve(steps + 1);

    const auto& start_local = start.local_pos();
    const auto& end_local = end.local_pos();
    const double du = end_local[0] - start_local[0];
    const double dv = end_local[1] - start_local[1];

    for (int i = 0; i <= steps; ++i) {
        const double t = static_cast<double>(i) / steps;
        const double u = start_local[0] + t * du;
        const double v = start_local[1] + t * dv;
        const auto local = ParamPoint2(u, v);
        points_.push_back(surface_->evaluate(local));
    }
}

void GeodesicCurve::compute_smooth_geodesic(
    const GeometricPoint<2, 3, WorldSpaceTag>& start,
    const GeometricPoint<2, 3, WorldSpaceTag>& end
) {
    // For spheres, compute great circle arc
    const auto start_pos = start.world_pos();
    const auto end_pos = end.world_pos();
    const double radius = start_pos.length();  // Assuming sphere centered at origin

    // Get normalized vectors to start and end points
    const WorldVector3 v1 = start_pos * (1.0 / radius);
    const WorldVector3 v2 = end_pos * (1.0 / radius);

    // Compute angle between vectors
    const double cos_angle = std::clamp(v1.dot(v2), -1.0, 1.0);
    const double angle = std::acos(cos_angle);

    // Sample points along great circle
    constexpr int steps = 20;  // Can be fewer points since this is exact
    points_.clear();
    points_.reserve(steps + 1);

    for (int i = 0; i <= steps; ++i) {
        const double t = static_cast<double>(i) / steps;
        const double current_angle = angle * t;
        const double sin_angle = std::sin(angle);

        // Spherical linear interpolation (SLERP)
        if (sin_angle > 1e-10) {
            const double sin_current = std::sin(current_angle);
            const double sin_remaining = std::sin(angle - current_angle);
            const WorldVector3 interp = 
                (v1 * sin_remaining + v2 * sin_current) * (radius / sin_angle);
            points_.push_back(surface_->evaluate(surface_->world_to_param(interp)));
        } else {
            // Points are very close, use linear interpolation
            const WorldVector3 interp = start_pos * (1.0 - t) + end_pos * t;
            points_.push_back(surface_->evaluate(surface_->world_to_param(interp)));
        }
    }
}

