void TransitionPath::add_segment(
    std::shared_ptr<Surface3D> surface,
    const ParamPoint1& t_start, const ParamPoint1& t_end,
    const ParamPoint2& uv_start, const ParamPoint2& uv_end,
    const WorldVector3& direction
) {
    if (!surface) {
        throw std::invalid_argument("Surface pointer cannot be null");
    }

    auto segment = std::make_unique<PathSegment>(surface);

    // For flat patches and developable surfaces, we only need start and end points
    if (surface->surface_type() == SurfaceType::Developable) {
        segment->add_point(t_start, uv_start);
        segment->add_point(t_end, uv_end);
        segments_.push_back(std::move(segment));
        return;
    }

    // For surfaces with path solvers, try to use exact intersection
    if (auto solver = surface->get_path_solver()) {
        const auto start_pos = surface->evaluate(uv_start).world_pos();
        if (auto intersection = (*solver)(start_pos, direction, 1000.0)) {
            segment->add_point(t_start, uv_start);
            // Use intersection point for exact boundary crossing
            const auto& hit_pos = intersection->position();
            const auto hit_params = surface->world_to_param(hit_pos);
            segment->add_point(t_end, ParamPoint2(hit_params.u(), hit_params.v()));
            segments_.push_back(std::move(segment));
            return;
        }
    }

    // Fallback to adaptive sampling based on surface curvature
    int num_points = BASE_TRANSITION_POINTS;

    // Get surface properties at start
    const auto geom = surface->evaluate(uv_start);
    const auto& derivs = geom.derivatives();
    const auto& du_vec = derivs[0];
    const auto& dv_vec = derivs[1];
    const auto normal = du_vec.crossed(dv_vec).normalized();
    const double curvature = std::abs(surface->gaussian_curv_func_(uv_start));
    if (curvature > CURVATURE_EPSILON) {
        num_points += static_cast<int>(5.0 * std::sqrt(curvature));
    }

    // Pre-compute parameter deltas for interpolation
    const double dt_param = static_cast<double>(t_end) - static_cast<double>(t_start);
    const double du_param = uv_end.u() - uv_start.u();
    const double dv_param = uv_end.v() - uv_start.v();

    // Linear interpolation for transition paths
    for (int i = 0; i < num_points; ++i) {
        const double alpha = static_cast<double>(i) / (num_points - 1);
        segment->add_point(
            ParamPoint1(static_cast<double>(t_start) + dt_param * alpha),
            ParamPoint2(
                uv_start.u() + du_param * alpha,
                uv_start.v() + dv_param * alpha
            )
        );
    }

    segments_.push_back(std::move(segment));
}

TransitionPath::TransitionPath()
    : Path3D(
        std::bind(&TransitionPath::evaluate_position, this, std::placeholders::_1),
        std::bind(&TransitionPath::evaluate_tangent, this, std::placeholders::_1),
        std::bind(&TransitionPath::evaluate_normal, this, std::placeholders::_1)
    )
