            return derivs[0].crossed(derivs[1]).normalized();
        }
    }
    const auto& last = segments_.back();
    const auto& local = last->uv_values().front();
    const auto geom = last->surface()->evaluate(local);
    const auto& derivs = geom.derivatives();
    return derivs[0].crossed(derivs[1]).normalized();
}

