    constexpr int steps = 20;
    points_.clear();
    points_.reserve(steps + 1);

    const auto& start_local = start.local_pos();
    const auto& end_local = end.local_pos();
    const double du = end_local[0] - start_local[0];
    const double dv = end_local[1] - start_local[1];

