    const WorldPoint3 test_point(0.75, 0, 0.5);  // 0.75 = 0.5 + 0.5*0.5 (due to skew)
    const auto local = face->world_to_param(test_point);
    assert(approx_equal(local.u(), 0.5));
    assert(approx_equal(local.v(), 0.5));
