                if (v_at_t >= -parameter_bound_epsilon && v_at_t <= 1.0 + parameter_bound_epsilon) {
                    min_t = t;
                    hit_param = ParamIndex::U;
                    hit_bound = ParamBound::Upper;
                    edge_param = std::clamp(v_at_t, 0.0, 1.0);
                    found = true;
