                if (u_at_t >= -parameter_bound_epsilon && u_at_t <= 1.0 + parameter_bound_epsilon) {
                    min_t = t;
                    hit_param = ParamIndex::V;
                    hit_bound = ParamBound::Upper;
                    edge_param = std::clamp(u_at_t, 0.0, 1.0);
                    found = true;
