#include "shap/surface_point.hpp"
#include <stdexcept>

namespace shap {

std::unique_ptr<SurfacePath> SurfaceCollection::create_path(
    const SurfacePoint& start,
    const Vector& direction,
    double length
) const {
    // Create a transition path that can handle multiple surfaces
    auto path = std::make_unique<TransitionPath>();

    // Get starting surface and point
    Surface* current_surface = start.surface();
    if (!current_surface) {
        throw std::runtime_error("Invalid starting surface");
    }

    // Initialize parameters
    double t = 0.0;
    SurfacePoint current = start;
    Vector current_dir = direction;

    while (t < length) {
        // Find next transition point
        const SurfaceConnection* next_connection = nullptr;
        double dist_to_edge = std::numeric_limits<double>::max();

        // Check all connections from current surface
        for (const auto& conn : connections) {
            if (conn.surface1 == current_surface || conn.surface2 == current_surface) {
                // Simple approximation of distance to edge based on current point
                if (current.is_on_edge()) {
                    next_connection = &conn;
                    dist_to_edge = 0.0;
                    break;
                }
            }
        }

        if (next_connection) {
            // Add segment up to transition
            double segment_length = std::min(0.25, length - t);
            path->add_segment(
                std::shared_ptr<Surface>(current_surface, [](Surface*){}),
                t, t + segment_length,
                current.u(), std::min(1.0, current.u() + current_dir.x * segment_length),
                current.v(), current.v() + current_dir.y * segment_length,
                current_dir
            );

            // Transition to next surface
            current = next_connection->map_point(current);
            current_surface = current.surface();
            t += segment_length;
        } else {
            // Add segment on current surface
            double segment_length = std::min(0.25, length - t);
            path->add_segment(
                std::shared_ptr<Surface>(current_surface, [](Surface*){}),
                t, t + segment_length,
                current.u(), std::min(1.0, current.u() + current_dir.x * segment_length),
                current.v(), current.v() + current_dir.y * segment_length,
                current_dir
            );

            // Update current point
            current = current_surface->evaluate(
                std::min(1.0, current.u() + current_dir.x * segment_length),
                current.v() + current_dir.y * segment_length
            );
            t += segment_length;
        }
    }

    return path;
}

} // namespace shap"}

